            "text": "\define bar() macDefInWin

\define newtidtitle() macronewtidtitle -title (or empty row)aaa<<currentTiddler>>bbb

\define newTidActions()
<$action-setfield $tiddler="$:/state/SideEditor" text=<<unusedtitle>> newtid=yes/><$action-deletetiddler $tiddler="$:/temp/SideEditor/macrotext"/><$action-navigate $to=<<unusedtitle>> $scroll=yes/>
\end

\define newTid(tid)
<$list filter="""[{$:/state/SideEditor!!newtid}match[yes]]""" variable="_NUL">
<$vars
    tmptitle={{{ [[$tid$]get[text]splitregexp[\n]nth[1]] }}}
    title={{{ [[$tid$]get[text]splitregexp[\n]nth[1]!match[]else[$tid$]] }}}
    tags={{{ [[$tid$]get[text]splitregexp[\n]nth[2]] }}} >
<$vars text={{{ [[$tid$]get[text]removeprefix<tmptitle>trim[]removeprefix<tags>trim[]] ~[[$tid$]get[text]removeprefix<tmptitle>]}}} >
   <$action-setfield
      $tiddler=<<title>>
       tags=<<tags>>
       text=<<text>> />
   <$action-setfield
       $tiddler="$:/StoryList"
       list={{{ [{$:/StoryList!!list}split[$tid$]!is[blank]join<title>] }}} />
   <$action-navigate $to=<<title>> $scroll=yes/>
</$vars>
</$vars>
<$list filter="""[[$tid$]get[text]splitregexp[\n]nth[1]!match[]]""">
   <$action-sendmessage $message="tm-close-tiddler" $param="""$tid$"""/>
   <$action-deletetiddler $tiddler="""$tid$"""/>
</$list>
<$action-deletefield $tiddler="$:/state/SideEditor" newtid />
</$list>
\end

<$navigator story="$:/StoryList" history="$:/HistoryList" >
<$list filter="[<inWindow>match[true]]" variable="_NUL">
<div class="se-window">
<div class="se-toolbar">
<span class="se-toolbar-btns se-left">
<!--Closing window doesn't work here (but it does work for delete button)
<a href="javascript:open(location, '_self').close();">
<$button tooltip="Save and Close editor"
   actions="""<$action-deletetiddler $tiddler="$:/state/SideEditor"/><$action-deletetiddler $tiddler="$:/temp/SideEditor/macrotext"/>""" >
