      <$action-setfield
         $tiddler={{$:/state/SideEditor}}
         text={{$:/temp/SideEditor/macrotext}} />
   </$list>
   <$list filter="[{$:/state/SideEditor}!is[missing]]">
      <$macrocall $name=newTid tid={{$:/state/SideEditor}} />
   </$list>
   <$list filter="[{$:/state/SideEditor}is[missing]]">
      <$action-setfield
          $tiddler="$:/StoryList"
          list={{{ [{$:/StoryList!!list}split[$tid$]!is[blank]join<title>] }}} />
   </$list>
   {{$:/core/images/done-button}}
</$button>
</a>
-->

<$list filter="[{$:/state/SideEditor}tag[$:/tags/Macro]]" variable="_NUL">
