      $tiddler="$:/StoryList"
       list={{{ [{$:/StoryList!!list}split{$:/state/SideEditor}!is[blank]join[]] }}} />
   <$action-deletetiddler $tiddler={{$:/state/SideEditor}} />
   <$action-deletetiddler $tiddler="""$:/temp/SideEditor/macrotext"""/>
 </$button>

<$button tooltip="Save tiddler and Create new"
    actions="""<$action-setfield $tiddler="$:/state/SideEditor" text=<<unusedtitle>> newtid=yes /><$action-deletetiddler $tiddler="$:/temp/SideEditor/macrotext" /><$action-navigate $to=<<unusedtitle>> $scroll=yes/>""">
   {{$:/core/images/new-button}}
   <$macrocall $name=newTid tid={{$:/state/SideEditor}} />
   <$list filter="[{$:/state/SideEditor}tags[$:/tags/Macro]]" variable="_NUL">
      <$action-setfield
         $tiddler={{$:/state/SideEditor}}
         text={{$:/temp/SideEditor/macrotext}} />
   </$list>
</$button>

<a href="javascript:open(location, '_self').close();">
  <$button tooltip="Switch to in-tiddly SideEditor"
      actions="""<$action-setfield $tiddler="$:/state/SideEditor" display=block />"""
      style="transform: rotate(180deg);">
        {{$:/core/images/open-window}}
  </$button>
</a>
</span>
</div>

<table class="se-editor-table">
<tr>
<td style="min-height:100px;">
