   {{$:/core/images/delete-button}}
   <$action-sendmessage $message="tm-close-tiddler" $param="""$tid$"""/>
   <$action-deletetiddler $tiddler="""$tid$"""/>
   <$action-deletetiddler $tiddler="""$:/temp/SideEditor/macrotext"""/>
   <$action-deletetiddler $tiddler="""$:/state/SideEditor"""/>
</$button>

<$button tooltip="Save tiddler and Create new"
    actions="""<$action-setfield $tiddler="$:/state/SideEditor" text=<<unusedtitle>> newtid=yes /><$action-deletetiddler $tiddler="$:/temp/SideEditor/macrotext" />""">
   <<$extraActions$>>
   <$macrocall $name=newTid tid={{$:/state/SideEditor}} />
   <$action-navigate $to=<<unusedtitle>> $scroll=yes/>
   {{$:/core/images/new-button}}  
</$button>

<$button tooltip="Open in separate window">
   <$action-deletefield $tiddler="$:/state/SideEditor" display />
   <$set name=isMacro
      filter="""[{$:/state/SideEditor}tag[$:/tags/Macro]then[yes]else[no]]""">
   <$action-sendmessage
      $message="tm-open-window"
      $param="""$toedit$"""
      extraActions="$extraActions$"
      isMacro=<<isMacro>>
      inWindow=true
      template="$:/plugins/TWaddle/SideEditor/Window"
      height={{{[{$:/info/browser/screen/height}multiply{$:/plugins/TWaddle/SideEditor/Metrics!!top}divide[100]trunc[]subtract{$:/info/browser/screen/height}multiply[-1]subtract[100]]}}}
      width={{{[{$:/info/browser/screen/width}multiply{$:/plugins/TWaddle/SideEditor/Metrics!!left}divide[100]trunc[]subtract{$:/info/browser/screen/width}multiply[-1]subtract[50]]}}}
   />
   </$set>
   {{$:/core/images/open-window}}
</$button>
</span>
</div>

<table class="se-editor-table">
<tr>
<td style="min-width:1.5em; background:transparent;"></td>
<td style="min-height:100px;">
   <$edit-text tiddler="""$toedit$""" field="text" 
      tag="textarea" class="se-editor" default=""/>
</td>
<$list filter="""[{$:/state/SideEditor!!newtid}match[yes]]""">
<$list filter="""[[$toedit$]is[missing]] [[$toedit$]!has[text]] +[first[]]""">
   <td style="min-height:100px; background:white;">
      <div class="se-editor-placeholder">
         ''title row'': type title - OR - leave row __empty__ to accept "//$toedit$//"<br>
         ''tags row'': type tags and [&zwj;[bracketed tags]&zwj;] - OR - leave row __empty__<br>
         ''text'' ...
      </div>
   </td>
   <style>.se-editor-table td:nth-of-type(2) {position:absolute;}
.se-editor {background:transparent;}</style>
</$list>
</$list>
</tr>
</table>
\end

\define sideeditor(tid)
<div id="sideeditor">
<$list filter={{$:/state/SideEditor}}>
   <<se-resizer "left">><<se-resizer "top">>
</$list>
<div id="se-main">
   <$list filter="""[{$:/state/SideEditor}!tag[$:/tags/Macro]]""">
      <$macrocall $name=se-main
          tid="""$tid$""" toedit={{$:/state/SideEditor}}/>
   </$list>
   <$list filter="""[{$:/state/SideEditor}tag[$:/tags/Macro]]""">
      <$macrocall $name=se-main
         tid="""$tid$""" toedit="$:/temp/SideEditor/macrotext" extraActions=extraActions />
   </$list>
   <$list filter="[[$:/state/SideEditor]display[block]]">
      <style>{{$:/plugins/TWaddle/SideEditor/StylesheetActive}}</style>
   </$list>
</div>
</div>
\end",
