        "$:/plugins/ahahn/tinka/tinka-repackagePlugin.js": {
            "title": "$:/plugins/ahahn/tinka/tinka-repackagePlugin.js",
            "text": "/*\
title: $:/plugins/ahahn/tinka/tinka-repackagePlugin.js
type: application/javascript
module-type: widget

repackagePlugin action widget

<$repackagePlugin $plugin=<<target>> $repackage="yes" $diff=<<qualified-modify-diff>> />

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

var CommonAction = require("$:/plugins/ahahn/tinka/tinkaCommonAction.js").tinkaCommonAction;

  
var repackagePluginWidget = function(parseTreeNode,options) {
	this.initialise(parseTreeNode,options);
    this.setup(false,false,["$plugin","$create","$diff"],false);
};

/*
Inherit from the base widget class
*/
repackagePluginWidget.prototype = new CommonAction();

/*
Invoke the action associated with this widget
*/
repackagePluginWidget.prototype.invokeAction = function(triggeringWidget,event) {
  	var diff = {};
  	var title = "";
	this.actionPlugin = this.param["$plugin"];
	this.actionCreate = this.param["$create"];
  	this.actionDiff = this.param["$diff"];

  	if(this.actionPlugin) {
      title = this.actionPlugin;
      if(this.actionDiff) {
     	var tid = this.wiki.getTiddler(this.actionDiff);
        diff = tid || {};
      }
      
      if(this.actionCreate == "yes") {
        //create new plugin Tiddler with the data from the diff tiddler
        var pluginTid = {};
        
        title = diff.fields["create-title"] || this.actionPlugin;
        pluginTid.title = title;
        pluginTid["text"] =	"{\"tiddlers\": {}}";
        pluginTid["type"] = "application/json";
        pluginTid["author"] = diff.fields["create-author"];
        pluginTid["description"] = diff.fields["create-description"];
		pluginTid["name"] = diff.fields["create-name"];
        pluginTid["list"] = diff.fields["create-list"];
        pluginTid["plugin-type"] = diff.fields["create-plugin-type"];
        pluginTid["dependents"] = diff.fields["create-dependents"];
        pluginTid["version"] = diff.fields["create-version"];
        pluginTid["core-version"] = diff.fields["create-core-version"];
        this.wiki.addTiddler(new $tw.Tiddler(pluginTid));
      }
      
      //execute repackaging
      $tw.utils.repackPlugin(title,diff.fields.addTiddlers,diff.fields.removeTiddlers);
  	}
};

exports["tinka-repackagePlugin"] = repackagePluginWidget;

})();
",
            "type": "application/javascript",
            "module-type": "widget"
        },
        "$:/plugins/ahahn/tinka/tinka-saveTaglistToField.js": {
            "title": "$:/plugins/ahahn/tinka/tinka-saveTaglistToField.js",
            "text": "/*\
title: $:/plugins/ahahn/tinka/tinka-saveTaglistToField.js
type: application/javascript
module-type: widget

Saves a tiddlers list of tags to a csv field.

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

var CommonAction = require("$:/plugins/ahahn/tinka/tinkaCommonAction.js").tinkaCommonAction;

var SaveTaglistToFieldWidget = function(parseTreeNode,options) {
	this.initialise(parseTreeNode,options);
    this.setup(false,false,["$target","$tiddler","$field"],false);
};

/*
Inherit from the base widget class
*/
SaveTaglistToFieldWidget.prototype = new CommonAction();

/*
Invoke the action associated with this widget
*/
SaveTaglistToFieldWidget.prototype.invokeAction = function(triggeringWidget,event) {
	var taglist = [];
  	var field = "text";
  
    if(this.param["$target"]) {
    	var targetTags = this.wiki.getTiddler(this.param["$target"]);
      	
      	if(targetTags) {
      		taglist = targetTags.fields.tags || [];
      	}
    }
  
  	if(this.param["$field"]) {
  		field = this.param["$field"];
  	}
  
  	if(this.param["$tiddler"]) {
  		//save taglist in field on tiddler
      	this.wiki.setTextReference(this.param["$tiddler"]+ "!!" +field,taglist,this.getVariable("currentTiddler")); 
 	}

  return true; // Action was invoked
};

exports["tinka-saveTaglistToField"] = SaveTaglistToFieldWidget;

})();
",
            "type": "application/javascript",
            "module-type": "widget"
        },
        "$:/plugins/ahahn/tinka/tinkaCommonAction.js": {
            "title": "$:/plugins/ahahn/tinka/tinkaCommonAction.js",
            "text": "/*\
title: $:/plugins/ahahn/tinka/tinkaCommonAction.js
type: application/javascript
module-type: widget

Tinka common action widget

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

var Widget = require("$:/core/modules/widgets/widget.js").widget;

var CommonActionWidget = function(parseTreeNode,options) {
	this.initialise(parseTreeNode,options);
};

/*
Inherit from the base widget class
*/
CommonActionWidget.prototype = new Widget();

/*
Setup an action widget with these properties
*/
CommonActionWidget.prototype.setup = function(allowPropagation,doRenderChildren,preparedParams,refreshOnAttributeChange) {
	this.doRenderChildren = doRenderChildren;
	this.preparedParams = preparedParams;
	this.allowPropagation = allowPropagation;
	this.refreshOnAttributeChange = refreshOnAttributeChange;
};
  
  
/*
Render this widget into the DOM
*/
CommonActionWidget.prototype.render = function(parent,nextSibling) {
	this.parentDomNode = parent;
	this.computeAttributes();
	this.execute();
  	if(this.doRenderChildren) {
		this.renderChildren(parent,nextSibling);
  	}
};

/*
Compute the internal state of the widget
*/
CommonActionWidget.prototype.execute = function() {
    this.processAttributes();
	// Construct the child widgets
	if(this.doRenderChildren) {
		this.makeChildWidgets();
	}
};
	
/*
Compute the values of our attributes
*/
CommonActionWidget.prototype.processAttributes = function() {
	var self = this;
	this.computeAttributes();
	this.param = {};

    if(this.isObject(this.preparedParams) && !this.isArray(this.preparedParams)) {
        $tw.utils.each(this.preparedParams,function(def, name) {
            self.param[name] = self.getAttribute(name,def);
        });
    }
    else {
        $tw.utils.each(this.preparedParams,function(name) {
            self.param[name] = self.getAttribute(name);
        });
    }
};

CommonActionWidget.prototype.allowActionPropagation = function() {
	return this.allowPropagation;	
};

CommonActionWidget.prototype.isEmptyObject = function(obj) {
	for(var prop in obj) {
        if(obj.hasOwnProperty(prop))
            return false;
    }

    return true;
};

CommonActionWidget.prototype.isObject = function(obj) {
    return ((obj !== null) && (typeof obj === 'object'));
};

CommonActionWidget.prototype.isArray = function(obj) {
    return (Object.prototype.toString.call(obj) === '[object Array]');
};

/*
Selectively refreshes the widget if needed. Returns true if the widget or any of its children needed re-rendering
*/
CommonActionWidget.prototype.refresh = function(changedTiddlers) {
  	var changedAttributes = this.computeAttributes();
		
	if(!this.isEmptyObject(changedAttributes) && this.refreshOnAttributeChange) {
		this.refreshSelf();
		return true;
    }
	else if (!this.isEmptyObject(changedAttributes)) {
		this.processAttributes();
	}
	
	return this.refreshChildren(changedTiddlers);		
};

exports.tinkaCommonAction = CommonActionWidget;

})();
