title: $:/core/modules/editor/operations/text/save-all.js
type: application/javascript
module-type: texteditoroperation

Text editor operation to select the entire text and save it to a specified tiddler and field

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports["save-all"] = function(event,operation) {
	var tiddler = event.paramObject.tiddler,
		field = event.paramObject.field || "text";
	if(tiddler && field) {
		operation.cutStart = 0;
		operation.cutEnd = operation.text.length;
		this.wiki.setText(tiddler, field, null, operation.text );
	}
};

})();"},"$:/core/modules/editor/operations/text/text-indent.js":{"title":"$:/core/modules/editor/operations/text/text-indent.js","created":"20240313012229071","modified":"20240324113408589","module-type":"texteditoroperation","tags":"首行缩进两字符 首行缩进和空行","type":"application/javascript","text":"/*\
title: $:/core/modules/editor/operations/text/text-indent.js
type: application/javascript
module-type: texteditoroperation

Text editor operation to add a prefix to the selected lines

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports["text-indent"] = function(event,operation) {
	var targetCount = parseInt(event.paramObject.count + "",10);
	// Cut just past the preceding line break, or the start of the text
	operation.cutStart = $tw.utils.findPrecedingLineBreak(operation.text,operation.selStart);
	// Cut to just past the following line break, or to the end of the text
	operation.cutEnd = $tw.utils.findFollowingLineBreak(operation.text,operation.selEnd);
	// Compose the required prefix
	var prefix = $tw.utils.repeat(event.paramObject.character,targetCount);
	// Process each line
	var lines = operation.text.substring(operation.cutStart,operation.cutEnd).split(/\r?\n/mg);
	$tw.utils.each(lines,function(line,index) {
		// Remove and count any existing prefix characters
		var count = 0;
		while($tw.utils.startsWith(line,event.paramObject.character)) {
			line = line.substring(event.paramObject.character.length);
			count++;
		}
		// Remove any whitespace
		while(line.charAt(0) === " ") {
			line = line.substring(1);
		}
		// We're done if we removed the exact required prefix, otherwise add it
		if(count !== targetCount) {
			// Apply the prefix
			line =  prefix + line;
		}
		// Save the modified line
		lines[index] = line;
	});
	// Stitch the replacement text together and set the selection
	operation.replacement = lines.join("\n");
	if(lines.length === 1) {
		operation.newSelStart = operation.cutStart + operation.replacement.length;
		operation.newSelEnd = operation.newSelStart;
	} else {
		operation.newSelStart = operation.cutStart;
		operation.newSelEnd = operation.newSelStart + operation.replacement.length;
	}
};

})();
"},"$:/core/ui/Buttons/blank-line":{"title":"$:/core/ui/Buttons/blank-line","caption":"{{$:/core/images/blank-line}} 每段空一行","condition":"[<targetTiddler>!has[type]] [<targetTiddler>type[text/vnd.tiddlywiki]] [<targetTiddler>type[application/x-tiddler-dictionary]]","created":"20240311232118077","description":"每段空一行","icon":"$:/core/images/blank-line","modified":"20240316103213246","shortcuts":"((blank-line))","tags":"$:/tags/EditorToolbar 按钮 空行 首行缩进和空行","text":"\define temp() $:/temp/blank-line
