title: $:/core/modules/widgets/chinese-calendar.js  
type: application/javascript  
module-type: widget  

中文日历widget - 主要渲染逻辑  
\*/  

"use strict";  

var Widget = require("$:/core/modules/widgets/widget.js").widget;  
var CalendarCell = require("$:/core/modules/widgets/chinese-calendar/calendar-cell.js");  
var CalendarData = require("$:/core/modules/widgets/chinese-calendar/calendar-data.js");  

var ChineseCalendarWidget = function(parseTreeNode,options) {  
    this.initialise(parseTreeNode,options);  
};  

ChineseCalendarWidget.prototype = new Widget();  

ChineseCalendarWidget.prototype.render = function(parent,nextSibling) {  
    this.parentDomNode = parent;  
    this.computeAttributes();  
    this.execute();  

    var calendarNode = this.document.createElement("div");  
    calendarNode.setAttribute("class", "tc-chinese-calendar");  

    this.generateCalendar(calendarNode);  

    parent.insertBefore(calendarNode,nextSibling);  
    this.domNodes.push(calendarNode);  
};  

ChineseCalendarWidget.prototype.execute = function() {  
    this.year = parseInt(this.getAttribute("year", new Date().getFullYear().toString()));  
    this.month = parseInt(this.getAttribute("month", (new Date().getMonth() + 1).toString()));  
    this.showLunar = this.getAttribute("lunar", "yes") === "yes";  
    this.showHolidays = this.getAttribute("holidays", "yes") === "yes";  
    this.mondayFirst = this.getAttribute("monday-first", "no") === "yes";  
    this.highlightToday = this.getAttribute("highlight-today", "yes") === "yes";  
    this.annotationFilter = this.getAttribute("annotation-filter", "");  

    var today = new Date();  
    this.todayYear = today.getFullYear();  
    this.todayMonth = today.getMonth() + 1;  
    this.todayDate = today.getDate();  

    // 初始化辅助模块  
    this.cellHelper = new CalendarCell(this);  
    this.dataHelper = new CalendarData(this);  
};  

ChineseCalendarWidget.prototype.generateCalendar = function(container) {  
    var monthNames = ["一月", "二月", "三月", "四月", "五月", "六月",   
                     "七月", "八月", "九月", "十月", "十一月", "十二月"];  

    var dayNames = this.mondayFirst ?   
        ["一", "二", "三", "四", "五", "六", "日"] :   
        ["日", "一", "二", "三", "四", "五", "六"];  

    // 创建标题  
    var title = this.document.createElement("h3");  
    title.setAttribute("class", "tc-calendar-title");  
    title.appendChild(this.document.createTextNode(this.year + "年" + monthNames[this.month - 1]));  
    container.appendChild(title);  

    // 创建表格  
    var table = this.document.createElement("table");  
    table.setAttribute("class", "tc-calendar-table");  

    // 创建表头  
    var thead = this.document.createElement("thead");  
    var headerRow = this.document.createElement("tr");  
    dayNames.forEach(function(dayName) {  
        var th = this.document.createElement("th");  
        th.appendChild(this.document.createTextNode(dayName));  
        headerRow.appendChild(th);  
    }, this);  
    thead.appendChild(headerRow);  
    table.appendChild(thead);  

    // 创建表体  
    this.generateCalendarBody(table);  

    container.appendChild(table);  
};  

ChineseCalendarWidget.prototype.generateCalendarBody = function(table) {  
    var tbody = this.document.createElement("tbody");  
    var firstDay = new Date(this.year, this.month - 1, 1).getDay();  

    if(this.mondayFirst) {  
        firstDay = (firstDay === 0) ? 6 : firstDay - 1;  
    }  

    var daysInMonth = new Date(this.year, this.month, 0).getDate();  
    var date = 1;  

    for(var week = 0; week < 6; week++) {  
        var row = this.document.createElement("tr");  
        for(var day = 0; day < 7; day++) {  
            var cell = this.document.createElement("td");  
            if(week === 0 && day < firstDay || date > daysInMonth) {  
                cell.setAttribute("class", "tc-calendar-empty");  
            } else {  
                this.cellHelper.createDateCell(cell, date);  
                date++;  
            }  
            row.appendChild(cell);  
        }  
        tbody.appendChild(row);  
        if(date > daysInMonth) break;  
    }  

    table.appendChild(tbody);  
};  

ChineseCalendarWidget.prototype.refresh = function(changedTiddlers) {  
    var changedAttributes = this.computeAttributes();  
    if(changedAttributes.year || changedAttributes.month ||   
       changedAttributes.lunar || changedAttributes.holidays ||  
       changedAttributes["monday-first"] || changedAttributes["highlight-today"] ||  
       changedAttributes["annotation-filter"]) {  
        this.refreshSelf();  
        return true;  
    } else {  
        return false;  
    }  
};  

exports["chinese-calendar"] = ChineseCalendarWidget;"},"$:/core/modules/widgets/chinese-calendar/calendar-annotations.js":{"title":"$:/core/modules/widgets/chinese-calendar/calendar-annotations.js","created":"20250606104318418","modified":"20250613032607554","module-type":"library","tags":"中文日历","type":"application/javascript","text":"/*\  
title: $:/core/modules/widgets/chinese-calendar/calendar-annotations.js  
type: application/javascript  
module-type: library  

中文日历 - 批注处理模块  
\*/  

"use strict";  

var CalendarAnnotations = function(widget) {  
    this.widget = widget;  
};  

CalendarAnnotations.prototype.handleAnnotations = function(cell, currentDate, cellClasses) {  
    if(!this.widget.annotationFilter) return;  

    var annotations = this.getAnnotations(currentDate);  
    if(annotations && annotations.length > 0) {  
        if(annotations.length === 1) {  
            this.addSingleAnnotation(cell, annotations[0]);  
        } else {  
            this.addMultipleAnnotations(cell, annotations);  
        }  
        cellClasses.push("tc-calendar-annotated");  
    }  
};  

CalendarAnnotations.prototype.getAnnotations = function(date) {  
    var dateKey = this.widget.year + "-" +   
                  String(this.widget.month).padStart(2, '0') + "-" +   
                  String(date.getDate()).padStart(2, '0');  

    // 调试日志（可选，用于排查问题）  
    // console.log("查找批注，日期键:", dateKey);  
    // console.log("筛选器:", this.widget.annotationFilter.replace("{{date}}", dateKey));  

    var annotationTiddlers = this.widget.wiki.filterTiddlers(  
        this.widget.annotationFilter.replace("{{date}}", dateKey), this.widget);  

    // console.log("找到的批注条目:", annotationTiddlers);  

    if(annotationTiddlers.length > 0) {  
        var self = this;  
        return annotationTiddlers.map(function(title) {  
            var tiddler = self.widget.wiki.getTiddler(title);  
            return {  
                text: tiddler.fields.text || "",  
                color: tiddler.fields["annotation-color"] || "default",  
                tiddlerTitle: title  
            };  
        });  
    }  
    return null;  
};  

CalendarAnnotations.prototype.addSingleAnnotation = function(cell, annotation) {  
    this.createAnnotationButton(cell, annotation, annotation.tiddlerTitle);  
};  

CalendarAnnotations.prototype.addMultipleAnnotations = function(cell, annotations) {  
    // 直接创建多批注按钮，简化逻辑  
    this.createMultiAnnotationButton(cell, annotations);  
};  

CalendarAnnotations.prototype.createAnnotationButton = function(cell, annotation, targetTitle) {  
    var self = this;  
    var svgButton = this.widget.document.createElement("div");  
    svgButton.setAttribute("class", "tc-calendar-annotation-button");  

    if(annotation.color && annotation.color !== "default") {  
        svgButton.classList.add("tc-calendar-annotation-" + annotation.color);  
    }  

    // 只使用条目中设置的图标，不提供硬编码默认值  
    var iconTiddler = this.widget.wiki.getTiddler("$:/core/images/calendar-annotation");  
    if(iconTiddler && iconTiddler.fields.text) {  
        svgButton.innerHTML = iconTiddler.fields.text;  
    } else {  
        // 如果没有图标条目，使用简单的文本标记  
        svgButton.textContent = "●";  
        svgButton.style.fontSize = "8px";  
        svgButton.style.lineHeight = "8px";  
        svgButton.style.textAlign = "center";  

// 根据批注颜色设置文本颜色  
if(annotation.color && annotation.color !== "default") {  
    // 使用CSS中定义的颜色映射  
    var colorMap = {  
        "red": "#dc3545",  
        "blue": "#007bff",   
        "green": "#28a745",  
        "yellow": "#ffc107",  
        "orange": "#fd7e14",  
        "purple": "#6f42c1"  
    };  
    svgButton.style.color = colorMap[annotation.color] || "#5797d8";  
} else {  
    svgButton.style.color = "#5797d8"; // 默认颜色  
}
    }  

    // 创建弹窗内容  
    var popupContent = this.widget.document.createElement("div");  
    popupContent.setAttribute("class", "tc-calendar-annotation-popup");  
    popupContent.style.display = "none";  

    var textLines = annotation.text.split('\n');  
    for(var i = 0; i < textLines.length; i++) {  
        if(i > 0) {  
            popupContent.appendChild(this.widget.document.createElement("br"));  
        }  
        popupContent.appendChild(this.widget.document.createTextNode(textLines[i]));  
    }  

    var isPopupVisible = false;  

    // 悬停显示功能  
    svgButton.addEventListener("mouseenter", function() {  
        popupContent.style.display = "block";  
        isPopupVisible = true;  
    });  

    svgButton.addEventListener("mouseleave", function() {  
        setTimeout(function() {  
            if(!popupContent.matches(':hover')) {  
                popupContent.style.display = "none";  
                isPopupVisible = false;  
            }  
        }, 100);  
    });  

    popupContent.addEventListener("mouseenter", function() {  
        popupContent.style.display = "block";  
        isPopupVisible = true;  
    });  

    popupContent.addEventListener("mouseleave", function() {  
        popupContent.style.display = "none";  
        isPopupVisible = false;  
    });  

    // 点击功能 - 跳转到批注条目  
    svgButton.addEventListener("click", function(event) {  
        event.stopPropagation();  

        // 如果有批注条目标题，则导航到该条目  
        if(targetTitle) {  
            self.widget.dispatchEvent({  
                type: "tm-navigate",  
                navigateTo: targetTitle,  
                navigateFromTitle: self.widget.getVariable("currentTiddler")  
            });  
        } else {  
            // 如果没有条目标题，则切换弹窗显示  
            if(isPopupVisible) {  
                popupContent.style.display = "none";  
                isPopupVisible = false;  
            } else {  
                popupContent.style.display = "block";  
                isPopupVisible = true;  
            }  
        }  
    });  

    cell.appendChild(svgButton);  
    cell.appendChild(popupContent);  
};  

CalendarAnnotations.prototype.createMultiAnnotationButton = function(cell, annotations) {  
    var self = this;  
    var svgButton = this.widget.document.createElement("div");  
    svgButton.setAttribute("class", "tc-calendar-annotation-button tc-calendar-multi-annotation");  

    // 使用第一个批注的颜色  
    if(annotations[0].color && annotations[0].color !== "default") {  
        svgButton.classList.add("tc-calendar-annotation-" + annotations[0].color);  
    }  

    // 使用相同的图标处理逻辑  
    var iconTiddler = this.widget.wiki.getTiddler("$:/core/images/calendar-annotation");  
    if(iconTiddler && iconTiddler.fields.text) {  
        svgButton.innerHTML = iconTiddler.fields.text;  
    } else {  
        // 如果没有图标条目，使用简单的文本标记  
        svgButton.textContent = "●";  
        svgButton.style.fontSize = "8px";  
        svgButton.style.lineHeight = "8px";  
        svgButton.style.textAlign = "center";  

// 根据批注颜色设置文本颜色  
if(annotation.color && annotation.color !== "default") {  
    // 使用CSS中定义的颜色映射  
    var colorMap = {  
        "red": "#dc3545",  
        "blue": "#007bff",   
        "green": "#28a745",  
        "yellow": "#ffc107",  
        "orange": "#fd7e14",  
        "purple": "#6f42c1"  
    };  
    svgButton.style.color = colorMap[annotation.color] || "#5797d8";  
} else {  
    svgButton.style.color = "#5797d8"; // 默认颜色  
}
    }  

    // 设置相对定位以便添加徽章  
    svgButton.style.position = "relative";  

    // 添加多批注数量徽章  
    var countBadge = this.widget.document.createElement("span");  
    countBadge.textContent = annotations.length.toString();  
    countBadge.style.cssText = "position:absolute;top:-5px;right:-5px;background:#ff4444;color:white;border-radius:50%;width:12px;height:12px;font-size:8px;text-align:center;line-height:12px;font-weight:bold;";  
    svgButton.appendChild(countBadge);  

    // 创建多批注选择菜单  
    var menuContent = this.widget.document.createElement("div");  
    menuContent.setAttribute("class", "tc-calendar-annotation-menu");  
    menuContent.style.display = "none";  

    annotations.forEach(function(annotation, index) {  
        var menuItem = self.widget.document.createElement("div");  
        menuItem.setAttribute("class", "tc-calendar-annotation-menu-item");  

        var preview = annotation.text.length > 20 ?   
                     annotation.text.substring(0, 20) + "..." :   
                     annotation.text;  
        menuItem.appendChild(self.widget.document.createTextNode(preview));  

        menuItem.addEventListener("click", function(event) {  
            event.stopPropagation();  
            if(annotation.tiddlerTitle) {  
                self.widget.dispatchEvent({  
                    type: "tm-navigate",  
                    navigateTo: annotation.tiddlerTitle,  
                    navigateFromTitle: self.widget.getVariable("currentTiddler")  
                });  
            }  
            menuContent.style.display = "none";  
            isMenuVisible = false;  
        });  

        menuContent.appendChild(menuItem);  
    });  

    var isMenuVisible = false;  

    svgButton.addEventListener("click", function(event) {  
        event.stopPropagation();  
        if(isMenuVisible) {  
            menuContent.style.display = "none";  
            isMenuVisible = false;  
        } else {  
            menuContent.style.display = "block";  
            isMenuVisible = true;  
        }  
    });  

    // 点击其他地方关闭菜单  
    var closeMenuHandler = function() {  
        menuContent.style.display = "none";  
        isMenuVisible = false;  
    };  

    this.widget.document.addEventListener("click", closeMenuHandler);  

    cell.appendChild(svgButton);  
    cell.appendChild(menuContent);  
};  

module.exports = CalendarAnnotations;
"},"$:/core/modules/widgets/chinese-calendar/calendar-cell.js":{"title":"$:/core/modules/widgets/chinese-calendar/calendar-cell.js","created":"20250606104242135","modified":"20250613032607554","module-type":"library","tags":"中文日历","type":"application/javascript","text":"/*\  
title: $:/core/modules/widgets/chinese-calendar/calendar-cell.js
type: application/javascript  
module-type: library

中文日历 - 日期单元格处理模块  
\*/  

"use strict";  

var CalendarAnnotations = require("$:/core/modules/widgets/chinese-calendar/calendar-annotations.js");  

var CalendarCell = function(widget) {  
    this.widget = widget;  
    this.annotationHelper = new CalendarAnnotations(widget);  
};  

CalendarCell.prototype.createDateCell = function(cell, date) {  
    var currentDate = new Date(this.widget.year, this.widget.month - 1, date);  
    var isToday = this.widget.highlightToday &&   
                  this.widget.year === this.widget.todayYear &&   
                  this.widget.month === this.widget.todayMonth &&   
                  date === this.widget.todayDate;  

    var cellClasses = ["tc-calendar-cell"];  
    if(isToday) cellClasses.push("tc-calendar-today");  

    // 创建日期链接容器  
    var dateLink = this.createDateLink(date);  
    var cellContent = this.createCellContent(date, currentDate, cellClasses);  

    dateLink.appendChild(cellContent);  
    cell.appendChild(dateLink);  

    // 处理批注  
    this.annotationHelper.handleAnnotations(cell, currentDate, cellClasses);  

    cell.setAttribute("class", cellClasses.join(" "));  
};  

CalendarCell.prototype.createDateLink = function(date) {  
    var dateLink = this.widget.document.createElement("div");  
    dateLink.setAttribute("class", "tc-calendar-date-link");  

    var dateKey = this.widget.year + "年" +   
                  String(this.widget.month).padStart(2, '0') + "月" +   
                  String(date).padStart(2, '0') + "日";  

    var self = this;  
    dateLink.addEventListener("click", function(event) {  
        if(!event.target.closest('.tc-calendar-annotation-button')) {  
            event.stopPropagation();  
            self.widget.dispatchEvent({  
                type: "tm-navigate",  
                navigateTo: dateKey,  
                navigateFromTitle: self.widget.getVariable("currentTiddler")  
            });  
        }  
    });  

    return dateLink;  
};  

CalendarCell.prototype.createCellContent = function(date, currentDate, cellClasses) {  
    var cellContent = this.widget.document.createElement("div");  
    cellContent.setAttribute("class", "tc-calendar-cell-content");  

    // 日期数字  
    var dateDiv = this.widget.document.createElement("div");  
    dateDiv.setAttribute("class", "tc-calendar-date");  
    dateDiv.appendChild(this.widget.document.createTextNode(date.toString()));  
    cellContent.appendChild(dateDiv);  

    // 农历日期  
    if(this.widget.showLunar) {  
        var lunarDate = this.widget.dataHelper.getLunarDate(currentDate);  
        if(lunarDate) {  
            var lunarDiv = this.widget.document.createElement("div");  
            lunarDiv.setAttribute("class", "tc-calendar-lunar");  
            lunarDiv.appendChild(this.widget.document.createTextNode(lunarDate));  
            cellContent.appendChild(lunarDiv);  
        }  
    }  

    // 节假日  
    if(this.widget.showHolidays) {  
        var holiday = this.widget.dataHelper.getHoliday(currentDate);  
        if(holiday) {  
            var holidayDiv = this.widget.document.createElement("div");  
            holidayDiv.setAttribute("class", "tc-calendar-holiday");  
            holidayDiv.appendChild(this.widget.document.createTextNode(holiday));  
            cellContent.appendChild(holidayDiv);  
            cellClasses.push("tc-calendar-holiday-cell");  
        }  
    }  

    return cellContent;  
};  

module.exports = CalendarCell;"},"$:/core/modules/widgets/chinese-calendar/calendar-data.js":{"title":"$:/core/modules/widgets/chinese-calendar/calendar-data.js","created":"20250606104500507","modified":"20250613032607554","module-type":"library","tags":"中文日历","type":"application/javascript","text":"/*\  
title: $:/core/modules/widgets/chinese-calendar/calendar-data.js  
type: application/javascript  
module-type: library  

中文日历 - 数据获取模块  
\*/  

"use strict";  

var CalendarData = function(widget) {  
    this.widget = widget;  
};  

CalendarData.prototype.getLunarDate = function(date) {  
    var monthKey = date.getFullYear() + "-" + String(date.getMonth() + 1).padStart(2, '0');  
    var dayKey = String(date.getDate());  

    var lunarDataTiddler = this.widget.wiki.getTiddler("$:/data/lunar/" + monthKey);  

    if(lunarDataTiddler && lunarDataTiddler.fields.text) {  
        try {  
            var monthData = JSON.parse(lunarDataTiddler.fields.text);  
            return monthData[dayKey] || "";  
        } catch(e) {  
            return "";  
        }  
    }  
    return "";  
};  

CalendarData.prototype.getHoliday = function(date) {  
    var dateKey = date.getFullYear() + "-" +   
                  String(date.getMonth() + 1).padStart(2, '0') + "-" +   
                  String(date.getDate()).padStart(2, '0');  

    // 查找整年的节假日数据tiddler  
    var holidayDataTiddler = this.widget.wiki.getTiddler("$:/data/holidays/" + date.getFullYear());  

    if(holidayDataTiddler && holidayDataTiddler.fields.text) {  
        try {  
            var yearData = JSON.parse(holidayDataTiddler.fields.text);  
            return yearData[dateKey] || null;  
        } catch(e) {  
            return null;  
        }  
    }  

