title: $:/core/modules/widgets/gdstudio-audio.js
type: application/javascript
module-type: widget

GDStudio Audio Widget - è¿·ä½ éŸ³é¢‘æ’­æ”¾å™¨
\*/

"use strict";

var Widget = require("$:/core/modules/widgets/widget.js").widget;

var GDStudioAudioWidget = function(parseTreeNode,options) {
    this.initialise(parseTreeNode,options);
};

GDStudioAudioWidget.prototype = new Widget();

GDStudioAudioWidget.prototype.execute = function() {
    this.coverUrl = this.getAttribute("cover-url", "");
    this.musicUrl = this.getAttribute("music-url", "");
    this.songName = this.getAttribute("song-name", "æœªçŸ¥æ­Œæ›²");
    this.artist = this.getAttribute("artist", "æœªçŸ¥æ­Œæ‰‹");

    this.makeChildWidgets();
};

GDStudioAudioWidget.prototype.createDefaultCover = function() {
    return "data:image/svg+xml;charset=utf-8," + encodeURIComponent(
        '<svg xmlns="http://www.w3.org/2000/svg" width="100" height="100">' +
        '<rect width="100%" height="100%" fill="#f0f0f0"/>' +
        '</svg>'
    );
};

GDStudioAudioWidget.prototype.render = function(parent,nextSibling) {
    var self = this;
    this.parentDomNode = parent;
    this.computeAttributes();
    this.execute();

    var playerDiv = this.document.createElement("div");
    playerDiv.className = "gdstudio-mini-player";

    var coverContainer = this.document.createElement("div");
    coverContainer.className = "gdstudio-mini-cover";

    var coverImg = this.document.createElement("img");
    coverImg.src = this.coverUrl || this.createDefaultCover();
    coverImg.className = "gdstudio-mini-cover-img";

    var playButton = this.document.createElement("button");
    playButton.className = "gdstudio-mini-play-btn";
    playButton.innerHTML = "â–¶";

    coverContainer.appendChild(coverImg);
    coverContainer.appendChild(playButton);

    var infoSection = this.document.createElement("div");
    infoSection.className = "gdstudio-mini-info";

    var musicName = this.document.createElement("div");
    musicName.className = "gdstudio-music-name";
    musicName.textContent = this.songName + " - " + this.artist;

    var controls = this.document.createElement("div");
    controls.className = "gdstudio-mini-controls";

    var timeDisplay = this.document.createElement("span");
    timeDisplay.className = "gdstudio-time";
    timeDisplay.textContent = "0:00/0:00";

    var progressBar = this.document.createElement("div");
    progressBar.className = "gdstudio-progress";

    var progressFill = this.document.createElement("div");
    progressFill.className = "gdstudio-progress-fill";
    progressBar.appendChild(progressFill);

// æ·»åŠ ç‚¹å‡»äº‹ä»¶ç›‘å¬å™¨ï¼Œå…è®¸ç”¨æˆ·æ‰‹åŠ¨è°ƒèŠ‚æ’­æ”¾ä½ç½®
progressBar.addEventListener("click", function(e) {  
    if (audio.duration) {  
        var rect = progressBar.getBoundingClientRect();  
        var clickX = e.clientX - rect.left;  
        var percentage = clickX / rect.width;  
        var newTime = percentage * audio.duration;  
        audio.currentTime = newTime;  
    }  
});

    // åˆå¹¶çš„éŸ³é‡æ§åˆ¶æŒ‰é’®
// éŸ³é‡çš„ SVG ä»£ç ï¼Œä»æ¡ç›®textè·å–
const volumeSvg = this.wiki.getTiddlerText("$:/core/images/volume");
    var volumeBtn = this.document.createElement("button");
    volumeBtn.className = "gdstudio-volume-btn";
    volumeBtn.innerHTML = volumeSvg;

    // éŸ³é‡é¢æ¿
    var volumePanel = this.document.createElement("div");
    volumePanel.className = "gdstudio-volume-panel";

    var volumeSlider = this.document.createElement("input");
    volumeSlider.type = "range";
    volumeSlider.min = "0";
    volumeSlider.max = "100";
    volumeSlider.value = "50";
    volumeSlider.className = "gdstudio-volume-slider";
    volumePanel.appendChild(volumeSlider);

    controls.appendChild(timeDisplay);
    controls.appendChild(progressBar);
    controls.appendChild(volumeBtn);
    controls.appendChild(volumePanel);

    infoSection.appendChild(musicName);
    infoSection.appendChild(controls);

    var audio = this.document.createElement("audio");
    if(this.musicUrl) {
        audio.src = this.musicUrl;
    }
    audio.preload = "metadata";
    audio.volume = 0.5; // è®¾ç½®åˆå§‹éŸ³é‡

    playerDiv.appendChild(coverContainer);
    playerDiv.appendChild(infoSection);
    playerDiv.appendChild(audio);

    // äº‹ä»¶å¤„ç†
    var isPlaying = false;
    var volumeVisible = false;
    var isMuted = false;
    var previousVolume = 0.5;

    // æ’­æ”¾æ§åˆ¶
// æš‚åœæ’­æ”¾çš„ SVG ä»£ç ï¼Œä»æ¡ç›®textè·å–
const pauseSvg = this.wiki.getTiddlerText("$:/core/images/gdstudio-pause-music");
    playButton.addEventListener("click", function() {
        if (!audio.src) return;
        if (isPlaying) {
            audio.pause();
            playButton.innerHTML = "â–¶";
            coverImg.classList.remove("spinning");
        } else {
            audio.play();
            playButton.innerHTML = pauseSvg;
            coverImg.classList.add("spinning");
        }
        isPlaying = !isPlaying;
    });

    // åˆå¹¶çš„éŸ³é‡æŒ‰é’®æ§åˆ¶
    volumeBtn.addEventListener("click", function(e) {
        e.preventDefault();
        e.stopPropagation();

        if (!volumeVisible) {
            // ç¬¬ä¸€æ¬¡ç‚¹å‡»ï¼šæ˜¾ç¤ºéŸ³é‡æ»‘å—
            volumeVisible = true;
            volumePanel.classList.add("visible");
        } else {
            // éŸ³é‡æ»‘å—å·²æ˜¾ç¤ºæ—¶ï¼šåˆ‡æ¢é™éŸ³çŠ¶æ€
            if (isMuted) {
                // å–æ¶ˆé™éŸ³
                audio.volume = previousVolume;
                volumeSlider.value = previousVolume * 100;
                volumeBtn.innerHTML = volumeSvg;
                isMuted = false;
            } else {
                // é™éŸ³
                previousVolume = audio.volume;
                audio.volume = 0;
                volumeSlider.value = 0;
                volumeBtn.innerHTML = "ğŸ”‡";
                isMuted = true;
            }
        }
    });

    // éŸ³é‡æ»‘å—æ§åˆ¶
    volumeSlider.addEventListener("input", function() {
        var volume = this.value / 100;
        audio.volume = volume;

        if (volume === 0) {
            volumeBtn.innerHTML = "ğŸ”‡";
            isMuted = true;
        } else {
            volumeBtn.innerHTML = volumeSvg;
            isMuted = false;
            if (volume > 0) {
                previousVolume = volume;
            }
        }
    });

    // ç‚¹å‡»å¤–éƒ¨å…³é—­éŸ³é‡é¢æ¿
    this.document.addEventListener("click", function(e) {
        if (!volumeBtn.contains(e.target) && !volumePanel.contains(e.target)) {
            volumeVisible = false;
            volumePanel.classList.remove("visible");
        }
    });

    // æ—¶é—´æ›´æ–°
    audio.addEventListener("timeupdate", function() {
        if (audio.duration) {
            var progress = (audio.currentTime / audio.duration) * 100;
            progressFill.style.width = progress + "%";

            var currentMin = Math.floor(audio.currentTime / 60);
            var currentSec = Math.floor(audio.currentTime % 60);
            var totalMin = Math.floor(audio.duration / 60);
            var totalSec = Math.floor(audio.duration % 60);

            timeDisplay.textContent = currentMin + ":" + (currentSec < 10 ? "0" : "") + currentSec + 
                                    "/" + totalMin + ":" + (totalSec < 10 ? "0" : "") + totalSec;
        }
    });

    audio.addEventListener("ended", function() {
        playButton.innerHTML = "â–¶";
        coverImg.classList.remove("spinning");
        isPlaying = false;
    });

    parent.insertBefore(playerDiv, nextSibling);
    this.domNodes.push(playerDiv);
};

GDStudioAudioWidget.prototype.refresh = function(changedTiddlers) {
    var changedAttributes = this.computeAttributes();
    if (Object.keys(changedAttributes).length > 0) {
        this.refreshSelf();
        return true;
    }
    return this.refreshChildren(changedTiddlers);
};

exports["gdstudio-audio"] = GDStudioAudioWidget;
"},"$:/core/modules/widgets/lyric-display.js":{"title":"$:/core/modules/widgets/lyric-display.js","created":"20250529133501913","modified":"20250529144400784","module-type":"widget","tags":"åŠ¨æ€æ­Œè¯ gdstudio","type":"application/javascript","text":"/*\    
title: $:/core/modules/widgets/lyric-display.js    
type: application/javascript    
module-type: widget    

æ­Œè¯æ˜¾ç¤ºwidget    
\*/    
"use strict";    

var Widget = require("$:/core/modules/widgets/widget.js").widget;    

var LyricDisplayWidget = function(parseTreeNode,options) {    
    this.initialise(parseTreeNode,options);    
};    

LyricDisplayWidget.prototype = new Widget();    

LyricDisplayWidget.prototype.render = function(parent,nextSibling) {    
    this.parentDomNode = parent;    
    this.computeAttributes();    
    this.execute();    
    this.domNode = this.document.createElement("div");    
    this.domNode.className = this.getSingleLineMode() ? "lyric-display-container single-line-mode" : "lyric-display-container";    
    this.domNode.style.overflow = "hidden";    

    // åˆ›å»ºæ»šåŠ¨å®¹å™¨    
    this.scrollContainer = this.document.createElement("div");    
    this.scrollContainer.style.transition = "transform 0.3s ease";    
    this.scrollContainer.style.transform = "translateY(0px)";    
    this.domNode.appendChild(this.scrollContainer);    

    parent.insertBefore(this.domNode,nextSibling);    
    this.domNodes.push(this.domNode);    

    this.updateDisplay();    
};    

LyricDisplayWidget.prototype.execute = function() {    
    this.lyricTiddler = this.getAttribute("lyric-tiddler");    
    this.showLines = parseInt(this.getAttribute("show-lines", "3"));    
    this.singleLineMode = this.getAttribute("single-line-mode", "no");    
    this.currentLineState = this.getAttribute("current-line-state", "$:/state/gdstudio-api/current-lyric-line");    
    this.parsedLyrics = this.parseLyrics();    
    this.lastCurrentLineIndex = -1;    
};    

LyricDisplayWidget.prototype.getSingleLineMode = function() {  
    return this.singleLineMode === "yes" || this.singleLineMode === "true";  
};  

LyricDisplayWidget.prototype.isSmallScreen = function() {  
    return window.innerWidth <= 767;  
};  

LyricDisplayWidget.prototype.shouldShowOnlyCurrentLine = function() {  
    return this.getSingleLineMode() || this.isSmallScreen();  
};  

LyricDisplayWidget.prototype.getLineHeight = function() {    
    // å¦‚æœå·²ç»æœ‰æ­Œè¯è¡Œï¼Œç›´æ¥ä½¿ç”¨ç¬¬ä¸€è¡Œçš„é«˜åº¦    
    if (this.scrollContainer.children.length > 0) {    
        return this.scrollContainer.children[0].offsetHeight;    
    }    

    // å¦åˆ™åˆ›å»ºä¸´æ—¶å…ƒç´     
    var tempElement = this.document.createElement("div");    
    tempElement.className = "lyric-line";    
    tempElement.style.visibility = "hidden";    
    tempElement.style.position = "absolute";    
    tempElement.style.top = "-9999px";    
    tempElement.textContent = "æµ‹è¯•æ–‡æœ¬";    

    this.domNode.appendChild(tempElement);    
    var actualHeight = tempElement.offsetHeight;    
    this.domNode.removeChild(tempElement);    

    return actualHeight || 40;    
};    

LyricDisplayWidget.prototype.parseLyrics = function() {    
    if (!this.lyricTiddler) return [];    
    var lyricText = this.wiki.getTiddlerText(this.lyricTiddler, "");    
    return lyricText.split('\n')    
        .map(function(line) {    
            var match = line.match(/^\[([0-9]{2}):([0-9]{2})\.([0-9]{1,9})\]\s*(.*)$/);    
            if (!match) return null;    
            var timeInSeconds = parseInt(match[1]) * 60 + parseInt(match[2]) + parseInt(match[3]) / Math.pow(10, match[3].length);    
            return {    
                time: timeInSeconds,    
                text: match[4]    
            };    
        })    
        .filter(Boolean)    
        .sort(function(a, b) {    
            return a.time - b.time;    
        });    
};    

LyricDisplayWidget.prototype.updateDisplay = function() {    
    if (!this.domNode || !this.scrollContainer) return;    

    if (this.parsedLyrics.length === 0) {    
        this.scrollContainer.innerHTML = "";    
        this.scrollContainer.textContent = "æ— æ­Œè¯";    
        return;    
    }    

    var currentLineIndex = parseInt(this.wiki.getTiddlerText(this.currentLineState, "-1"));    

    if (this.shouldShowOnlyCurrentLine()) {  
        // å•è¡Œæ¨¡å¼ï¼šé‡æ–°æ¸²æŸ“å½“å‰è¡Œ  
        if (currentLineIndex !== this.lastCurrentLineIndex) {  
            this.renderCurrentLineOnly(currentLineIndex);  
        }  
    } else {  
        // å¤šè¡Œæ¨¡å¼ï¼šåŸæœ‰é€»è¾‘  
        if (this.scrollContainer.children.length === 0) {    
            this.renderAllLyrics();    
        }    

        if (!this.lineHeight) {    
            this.lineHeight = this.getLineHeight();    
        }    

        this.updateHighlight(currentLineIndex);    

        if (currentLineIndex >= 0 && currentLineIndex !== this.lastCurrentLineIndex) {    
            this.scrollToLine(currentLineIndex);    
        }    
    }  

    this.lastCurrentLineIndex = currentLineIndex;    
};    

LyricDisplayWidget.prototype.renderCurrentLineOnly = function(currentLineIndex) {  
    this.scrollContainer.innerHTML = "";  
    if (currentLineIndex >= 0 && currentLineIndex < this.parsedLyrics.length) {  
        var lineDiv = this.document.createElement("div");  
        lineDiv.className = "lyric-line lyric-current";  
        lineDiv.textContent = this.parsedLyrics[currentLineIndex].text;  
        this.scrollContainer.appendChild(lineDiv);  
    }  
};  

LyricDisplayWidget.prototype.renderAllLyrics = function() {    
    this.scrollContainer.innerHTML = "";    
    for (var i = 0; i < this.parsedLyrics.length; i++) {    
        var lineDiv = this.document.createElement("div");    
        lineDiv.className = "lyric-line";    
        lineDiv.textContent = this.parsedLyrics[i].text;    
        lineDiv.setAttribute("data-line-index", i);    
        this.scrollContainer.appendChild(lineDiv);    
    }    
};    

LyricDisplayWidget.prototype.updateHighlight = function(currentLineIndex) {    
    var lines = this.scrollContainer.querySelectorAll('.lyric-line');    
    for (var i = 0; i < lines.length; i++) {    
        var line = lines[i];    
        if (i === currentLineIndex) {    
            line.className = "lyric-line lyric-current";    
        } else {    
            line.className = "lyric-line";    
        }    
    }    
};    

LyricDisplayWidget.prototype.scrollToLine = function(lineIndex) {    
    if (lineIndex < 0) return;    

    if (!this.lineHeight) {    
        this.lineHeight = this.getLineHeight();    
    }    

    var centerOffset = Math.floor(this.showLines / 2);    
    var targetOffset = (lineIndex - centerOffset) * this.lineHeight;    

    var maxOffset = Math.max(0, (this.parsedLyrics.length - this.showLines) * this.lineHeight);    
    targetOffset = Math.max(0, Math.min(targetOffset, maxOffset));    

    this.scrollContainer.style.transform = "translateY(-" + targetOffset + "px)";    
};    

LyricDisplayWidget.prototype.refresh = function(changedTiddlers) {    
    var changedAttributes = this.computeAttributes();    

    if (changedAttributes["lyric-tiddler"] || changedTiddlers[this.lyricTiddler] ||   
        changedAttributes["single-line-mode"]) {    
        this.refreshSelf();    
        return true;    
    }    

    if (changedTiddlers[this.currentLineState]) {    
        var currentLineIndex = parseInt(this.wiki.getTiddlerText(this.currentLineState, "-1"));    
        if (currentLineIndex !== this.lastCurrentLineIndex) {    
            this.updateDisplay();    
        }    
        return true;    
    }    

    return false;    
};    

exports["lyric-display"] = LyricDisplayWidget;
"},"$:/core/modules/widgets/lyric-sync.js":{"title":"$:/core/modules/widgets/lyric-sync.js","created":"20250529133321082","modified":"20250529144406776","module-type":"widget","tags":"åŠ¨æ€æ­Œè¯ gdstudio","type":"application/javascript","text":"/*\
