  overflow: visible;
}.tc-editor-toolbar {
	position: -webkit-sticky;
	position: -moz-sticky;
	position: -o-sticky;
	position: -ms-sticky;
	position: sticky;
	top: 35px;
    background: <<colour tiddler-editor-background>>; 
	z-index: 500;
}.sticky-footer{
   position: sticky;
   bottom: 0;
   padding: 0 5px 5px 0;
   background: transparent;
	z-index: 500;
}/* 因已使用了标题吸顶不需要再设置 */
