exports.jsonGet= function(obj, index, stringify) {
	stringify = typeof(stringify) == "boolean" ? stringify : true;
	if(!obj) throw "jsonGet error, no object";
	if(!index || index.length == 0 || index == "" || index == "/") {
		console.log( "jsonGet error - Missing or invalid index: "+index);
		return;
	}

	var value;
	if (!(index.charAt( 0 ) == '/')) {
		index = "/" + index;
	};
	if($tw.utils.hop(obj, index.substring(1))) {
		value = obj[index.substring(1)];
	}
	else if($tw.utils.hop(obj, index)) {
		value = obj[index] || "";
	}
	else if(pointer.has(obj,index)){
		value = pointer.get(obj,index); 
	}
	else {value = "";}
	if (stringify) return String(value) === value ? value : JSON.stringify(value, null, 2)
	else return value;	
};

