<<<"},"$:/plugins/kookma/garden/macros/clone-create":{"title":"$:/plugins/kookma/garden/macros/clone-create","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\define create-new-actions()
<$let
  new-code-title   = <<unusedtitle template:"$count:3$/code">> 
  new-remark-title = {{{ [<new-code-title>removesuffix[code]addsuffix[remark]] }}} 
  >
<$action-sendmessage $message="tm-new-tiddler" title=<<new-remark-title>> tags="Remark" />
<$action-sendmessage $message="tm-new-tiddler" title=<<new-code-title>> tags="Code"   description="what is this code?" keywords=""/>
</$let>	
\end	

\define create-new-example()
<$button class="tc-btn-invisible" tooltip="create new example" actions=<<create-new-actions>> >
{{$:/core/images/new-button}}
</$button>
\end

\define clone-this-actions()
<$let
  new-code-title      = <<unusedtitle template:"$count:3$/code">> 
  new-remark-title    = {{{ [<new-code-title>removesuffix[code]addsuffix[remark]] }}} 
  new-code-template   = <<__thisTiddler__>>
  new-remark-template = {{{ [<new-code-template>removesuffix[code]addsuffix[remark]] }}}
  >
<$action-sendmessage $message="tm-new-tiddler" $param=<<new-remark-template>> title=<<new-remark-title>>  />	
<$action-sendmessage $message="tm-new-tiddler" $param=<<new-code-template>>   title=<<new-code-title>>    />
</$let>	
\end	


\define clone-this-example(thisTiddler)
<$button class="tc-btn-invisible" tooltip="clone this example" actions=<<clone-this-actions>> >
{{$:/core/images/clone-button}}
</$button>
\end"},"$:/plugins/kookma/garden/macros/definitions":{"title":"$:/plugins/kookma/garden/macros/definitions","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\define stateTidSelectedCode()    $:/state/garden/selected-code
\define stateTidSelectedTag()     $:/state/garden/selected-tag
\define stateTidReorderExamples() $:/state/garden/reorder-items
\define sandboxTid()              $:/sandbox/garden/$(vv-exmp-tiddler)$

\define gardenUiTiddler()         $:/plugins/kookma/garden/config/UiTiddler"},"$:/plugins/kookma/garden/macros/filter-by-tag":{"title":"$:/plugins/kookma/garden/macros/filter-by-tag","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\define filter-by-tag()
\define radio-actions() <$action-deletetiddler $tiddler=<<stateTidSelectedCode>> />
<div class="twg-radio">
<$radio tiddler=<<stateTidSelectedTag>> field="text" value="Elementary" default="Elementary" actions=<<radio-actions>>> Elementary</$radio>
<$radio tiddler=<<stateTidSelectedTag>> field="text" value="Intermediate" default="" actions=<<radio-actions>>> Intermediate</$radio>
<$radio tiddler=<<stateTidSelectedTag>> field="text" value="Advanced" default="" actions=<<radio-actions>>> Advanced</$radio>
</div>
\end
"},"$:/plugins/kookma/garden/macros/main-ui":{"title":"$:/plugins/kookma/garden/macros/main-ui","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\whitespace trim

\define btn-edit-example()
<$button class="tc-btn-invisible" tooltip="edit code">{{$:/core/images/edit-button}}
<$action-setfield $tiddler=<<sandboxTid>> $field=text $value={{{ [<vv-exmp-tiddler>get[text]] }}}/>
</$button>
\end

\define btn-reset-example()
<$list filter="[<vv-edit-status>match[yes]then[no]else[yes]]" variable=disabled>
<$button class="tc-btn-invisible" disabled=<<disabled>> tooltip="restore original code">{{$:/core/images/refresh-button}}
<$action-deletetiddler $tiddler=<<sandboxTid>>/>
</$button></$list>
\end

\define btn-copy-example-to-clipboard()
<$button message="tm-copy-to-clipboard" class="tc-btn-invisible" tooltip="copy code to clipboard" 
         param={{{ [<sandboxTid>get[text]] :else[<stateTidSelectedCode>get[text]get[text]] }}} >
{{$:/core/images/copy-clipboard}}
</$button>
\end

<!-- create the main ui of garden -->
\define garden-ui(filter:'[all[shadows+tiddlers]tag[Code]]')
<$let vv-level-tag    = {{{ [<stateTidSelectedTag>get[text]] :else[[Elementary]] }}}
      vv-exmp-filter  = '[subfilter<__filter__>]:filter[tag<vv-level-tag>]'
      vv-exmp-tiddler = {{{ [<stateTidSelectedCode>get[text]] }}}
      vv-edit-status  = {{{ [<sandboxTid>is[tiddler]then[yes]else[no]] }}} > 

<!-- create the header, to select an example and navigate through them -->
<header class="twg-header">
<$select tiddler=<<stateTidSelectedCode>> default='default' class="twg-select">
<option value='default' disabled>Select an example</option>
<$list filter=<<vv-exmp-filter>> >
<option value=<<currentTiddler>>>
<$view field='description'><$view field=title/></$view>
</option>
</$list>
</$select>

<$let currentTiddler=<<vv-exmp-tiddler>> stateTid=<<stateTidSelectedCode>> >
<$macrocall $name=simple-navigation codeFilter=<<vv-exmp-filter>> class="twg-navigation"/>
</$let>
<div><<filter-by-tag>></div>
</header>

<!-- the Nav part implements buttons to create/clone/view buttons and edit/reset/copy code-->
<nav class="twg-nav">
<div class="twg-author-tools">
<<create-new-example>> | 
<$macrocall $name="clone-this-example" thisTiddler=<<vv-exmp-tiddler>> /> | 
<$macrocall $name="open-this-in-storyriver" thisTiddler=<<vv-exmp-tiddler>> />
</div>
<div>
<<btn-edit-example>> | <<btn-reset-example>> | <<btn-copy-example-to-clipboard>>
</div>
</nav>

<!-- display the body of code -->
<$tiddler tiddler={{{ [<vv-edit-status>match[yes]then<sandboxTid>else<vv-exmp-tiddler>] }}} >
<$transclude mode=block tiddler="$:/plugins/kookma/garden/templates/editor-viewer"/>
</$tiddler>

<!-- display the remark section of example (remark tiddler) -->
<section class="twg-remarks">
<$transclude tiddler={{{ [<vv-exmp-tiddler>removesuffix[code]addsuffix[remark]]}}} mode=block/>
</section>

</$let>
\end"},"$:/plugins/kookma/garden/macros/open-in-garden":{"title":"$:/plugins/kookma/garden/macros/open-in-garden","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\define open-in-garden-actions()
<$action-setfield $tiddler=<<stateTidSelectedCode>> text=<<vv-code-tiddler>> />
<$action-setfield $tiddler=<<stateTidSelectedTag>> $field=text $value=<<difficulty-level>> />
\end

\define open-in-garden()
<$let vv-code-tiddler     = {{{ [<currentTiddler>split[/]first[]addsuffix[/code]] }}} 
      vv-gardenui-tiddler = {{{ [<gardenUiTiddler>get[text]] }}}
      difficulty-level    = {{{ [<vv-code-tiddler>contains:tags[Elementary]then[Elementary]]
                            :else[<vv-code-tiddler>contains:tags[Intermediate]then[Intermediate]]
                            :else[<vv-code-tiddler>contains:tags[Advanced]then[Advanced]] }}}			
 >
Open this in&nbsp;
<$button class="tc-btn-invisible tc-tiddlylink" to=<<vv-gardenui-tiddler>> actions=<<open-in-garden-actions>> >
Garden
</$button>
</$let>
\end"},"$:/plugins/kookma/garden/macros/open-in-storyriver":{"title":"$:/plugins/kookma/garden/macros/open-in-storyriver","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\define open-this-in-storyriver(thisTiddler)
<$let
  code-tiddler   = <<__thisTiddler__>>
  remark-tiddler = {{{ [<code-tiddler>removesuffix[code]addsuffix[remark]] }}}
  >
<$button class="tc-btn-invisible" tooltip="open example code and remark in story river" actions='	
  <$action-navigate $to=<<remark-tiddler>> />
  <$action-navigate $to=<<code-tiddler>> $scroll="yes"/>
 '>
{{$:/plugins/kookma/garden/images/open-in-story}}
</$button>
</$let>	
\end"},"$:/plugins/kookma/garden/macros/settings/garden-ui-tiddler":{"title":"$:/plugins/kookma/garden/macros/settings/garden-ui-tiddler","code-body":"yes","tags":"","type":"text/vnd.tiddlywiki","text":"\import [all[tiddlers+shadows]tag[$:/tags/Macro/Garden]]

\define create-garden-ui-actions()
<$action-createtiddler $basetitle=<<vv-garden-ui-tiddler>> /> 
<$action-navigate $to=<<vv-garden-ui-tiddler>> $scroll="yes"/>
\end


<$let vv-garden-ui-tiddler={{{[<gardenUiTiddler>get[text]]}}} >
<$list filter="[<vv-garden-ui-tiddler>!is[missing]]">
Goto <$link/>
</$list>
<$list filter="[<vv-garden-ui-tiddler>is[missing]]">
Craete <$button class="tc-btn-invisible tc-tiddlylink" actions=<<create-garden-ui-actions>> >
<$text text=<<vv-garden-ui-tiddler>> />
</$button>
</$list>
</$let>
"},"$:/plugins/kookma/garden/macros/settings/tags-color":{"title":"$:/plugins/kookma/garden/macros/settings/tags-color","tags":"","type":"text/vnd.tiddlywiki","text":"\define reset-color(tiddler, color)
<$button class="tc-btn-invisible"> {{$:/core/images/refresh-button}}
<$action-setfield $tiddler=<<__tiddler__>> $field=color $value=<<__color__>> />
</$button>
\end

\define set-color(tiddler)
<$edit-text tiddler=<<__tiddler__>> tag=input field=color type=text />
<$edit-text tiddler=<<__tiddler__>> tag=input field=color  />
\end

|table-borderless twg-settings|k
|Elementary|<<set-color Elementary>>|<<reset-color Elementary #119dc0>>|
|Intermediate|<<set-color Intermediate>>|<<reset-color Intermediate #a5a01d>>|
|Advanced|<<set-color Advanced>>|<<reset-color Advanced #aa0877>>|
"},"$:/plugins/kookma/garden/macros/simple-navigation":{"title":"$:/plugins/kookma/garden/macros/simple-navigation","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\define simple-navigation(codeFilter:"", class:"twg-navigation")
<div class="$class$">
<$list filter="[subfilter<__codeFilter__>before<currentTiddler>]" variable="prevTiddler">
<$button tooltip=<<prevTiddler>> class="tc-btn-invisible">previous
<$action-setfield $tiddler=<<stateTid>> $field=text $value=<<prevTiddler>> />
</$button>
</$list>
<$list filter="[subfilter<__codeFilter__>after<currentTiddler>] [subfilter<__codeFilter__>before<currentTiddler>] +[count[]compare:integer:gteq[2]]" variable=ignore>|</$list>
<$list filter="[subfilter<__codeFilter__>after<currentTiddler>]" variable="nextTiddler">
<$button tooltip=<<nextTiddler>> class="tc-btn-invisible">next
<$action-setfield $tiddler=<<stateTid>> $field=text $value=<<nextTiddler>> />
</$button>
</$list>
</div>
\end"},"$:/plugins/kookma/garden/readme":{"title":"$:/plugins/kookma/garden/readme","tags":"","type":"text/vnd.tiddlywiki","text":";Tiddlywiki Garden
The garden is used to demonstrate, practice and learn, Filters, Wikitext, and CSS. Tiddlywiki garden inspired from [[CSS Zen Garden|https://en.wikipedia.org/wiki/CSS_Zen_Garden]].

; Code and demo
For learning plugin features, syntax, tutorial and examples see the plugin demo and code pages

* Demo: https://kookma.github.io/TW-Garden/
* Code: https://github.com/kookma/TW-Garden
"},"Reorder Examples in Garden":{"title":"Reorder Examples in Garden","tags":"","type":"text/vnd.tiddlywiki","text":"\import $:/plugins/kookma/garden/macros/definitions

\define sort-actions(sort-order:'')
<$set name=this-level-examples filter="[all[tiddlers+shadows]tag<level>$sort-order$sort[description]]">
<$action-setfield $tiddler=<<level>> $field=list $value=<<this-level-examples>> />
</$set>
\end


<$select tiddler=<<stateTidReorderExamples>> default="Elementary" class="twg-select">
<option value="" disabled>Select a level</option>
<$list filter="Elementary Intermediate Advanced">
<option><<currentTiddler>></option>
</$list>
</$select>
<$let level={{{ [<stateTidReorderExamples>get[text]else[Elementary]] }}} >
<$button actions=<<sort-actions>> tooltip="Sort ascending" >{{$:/core/images/down-arrow}}</$button> 
<$button actions=<<sort-actions "!">> tooltip="Sort descending" >{{$:/core/images/up-arrow}}</$button> 
<$button actions='<$action-deletefield $tiddler=<<level>> list/>' tooltip="Reset" >{{$:/core/images/refresh-button}}</$button> 

<div>

Sort examples in Garden by drag and drop

<$macrocall $name="list-tagged-draggable" tag=<<level>> itemTemplate="$:/plugins/kookma/garden/templates/item-template"/>
</div>
</$let>"},"$:/plugins/kookma/garden/shortcutkeys/open-garden":{"title":"$:/plugins/kookma/garden/shortcutkeys/open-garden","code-body":"yes","key":"((open-garden))","tags":"$:/tags/KeyboardShortcut","type":"text/vnd.tiddlywiki","text":"\import $:/plugins/kookma/garden/macros/definitions
<$navigator story="$:/StoryList" history="$:/HistoryList">
<$action-setfield $tiddler="$:/state/sidebar" text="no"/>
<$action-sendmessage $message="tm-close-all-tiddlers" />
<$action-navigate $to={{{ [<gardenUiTiddler>get[text]] }}} $scroll="yes"/>
</$navigator>
"},"$:/plugins/kookma/garden/styles/author-tools":{"title":"$:/plugins/kookma/garden/styles/author-tools","text":".twg-author-tools{
	visibility: hidden;
}","tags":"","type":"text/css"},"$:/plugins/kookma/garden/styles/code-tiddler-color":{"title":"$:/plugins/kookma/garden/styles/code-tiddler-color","tags":"$:/tags/Stylesheet","type":"text/vnd.tiddlywiki","text":"<$let color-scheme    = {{{ [[$:/palette]get[text]get[color-scheme]] }}}
      opacity         = {{{ [<color-scheme>match[dark]then[60]else[30]] }}}
			elementary-bg   = {{{ [[Elementary]get[color]addsuffix<opacity>] }}}
			intermediate-bg = {{{ [[Intermediate]get[color]addsuffix<opacity>] }}}
			advanced-bg     = {{{ [[Advanced]get[color]addsuffix<opacity>] }}}
  >
[data-tags*="Elementary"] {
/*  border-top: 2px solid #119DC0; */
	background:linear-gradient(to bottom, <<elementary-bg>>, <<colour tiddler-background>> 25%);
}
[data-tags*="Intermediate"] {
/*  border-top: 2px solid #A5A01D; */
	background:linear-gradient(to bottom, <<intermediate-bg>>, <<colour tiddler-background>> 25%);	
}
[data-tags*="Advanced"] {
/*  border-top: 0px solid #AA0877; */
	background:linear-gradient(to bottom, <<advanced-bg>>, <<colour tiddler-background>> 25%);	
}
</$let>"},"$:/plugins/kookma/garden/styles/code-tiddler":{"title":"$:/plugins/kookma/garden/styles/code-tiddler","text":".twg-code-tiddler h2{
	font-weight:500;
	font-size: 0.9rem;
}

.twg-code-tiddler-desc,
.twg-code-tiddler-code,
.twg-code-tiddler-remark {
	padding-left:1.5rem;
}","tags":"$:/tags/Stylesheet","type":"text/css"},"$:/plugins/kookma/garden/styles/editarea":{"title":"$:/plugins/kookma/garden/styles/editarea","tags":"$:/tags/Stylesheet","type":"text/vnd.tiddlywiki","text":".twg-wide-edit { 
	min-width:100%;
	min-height:240px;
	margin:0;
	resize:vertical;
	border-radius:5px;
	border:none;
	outline:none;
	font-family:{{$:/themes/tiddlywiki/vanilla/settings/codefontfamily}}
}
.twg-grid-code pre{
	margin:0;
	border:none;
	background-color:unset;
	min-width:100%;
	min-height:240px;
}"},"$:/plugins/kookma/garden/styles/footer":{"title":"$:/plugins/kookma/garden/styles/footer","text":".twg-footer {
	display:flex;
	justify-content: space-between;
	font-size:0.85em;
	margin-top:20px;
	margin-bottom:8px;
	padding-top:5px;
	border-top:0.5px solid #ccc;
}","tags":"$:/tags/Stylesheet","type":"text/css"},"$:/plugins/kookma/garden/styles/grid":{"title":"$:/plugins/kookma/garden/styles/grid","text":".twg-grid-container {
	display: grid;
	grid-template-columns: repeat(auto-fill, minmax( max(300px,48%), 1fr));
	grid-gap: 10px;
}
.twg-grid-column{
	border-radius:5px;
	border:1px solid #ccc;
	max-height:75vh;
	overflow:auto;
}

.twg-grid-code .hljs {
	height: 100%;
}
.twg-grid-rendered{
	padding:8px;
	max-width:100%;
}","tags":"$:/tags/Stylesheet","type":"text/css"},"$:/plugins/kookma/garden/styles/header":{"title":"$:/plugins/kookma/garden/styles/header","text":".twg-header {
	display: flex;
	flex-wrap: wrap;
	justify-content: flex-start;
	gap:10px;
	margin-bottom:5px;
}

.twg-header .twg-select{
	flex: 3 1 360px;
}

.twg-header .twg-navigation{
	flex: 1 1 120px;
}


.twg-select{
	outline: none;
	border-radius: 0.25rem;
	transition: border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
	padding:1px;
}","tags":"$:/tags/Stylesheet","type":"text/css"},"$:/plugins/kookma/garden/styles/hide-ui-buttons":{"title":"$:/plugins/kookma/garden/styles/hide-ui-buttons","tags":"$:/tags/Stylesheet","type":"text/vnd.tiddlywiki","text":"\import $:/plugins/kookma/garden/macros/definitions
<$list filter="[<gardenUiTiddler>get[text]]">
[data-tiddler-title^="<$view field=title/>"]	.tc-tiddler-title .tc-titlebar button:not(.tc-btn-\%24\%3A\%2Fcore\%2Fui\%2FButtons\%2Fclose)  {display:none;}
</$list> "},"$:/plugins/kookma/garden/styles/item-template":{"title":"$:/plugins/kookma/garden/styles/item-template","text":"/* Sort Examples */
.twg-item-list-title,
.twg-item-list-description {
	display:inline-block;
	white-space: nowrap;
	text-overflow: ellipsis;
	overflow-x:clip;
}
.twg-item-list-title {
	width:6rem;
}
.twg-item-list-description {
	max-width:calc(100% - 10rem);
}","tags":"$:/tags/Stylesheet","type":"text/css"},"$:/plugins/kookma/garden/styles/nav":{"title":"$:/plugins/kookma/garden/styles/nav","text":".twg-nav {
	display: flex;
	justify-content: space-between;
	gap:5px;
	margin-left:  8px;
