<<<"},"$:/plugins/kookma/kara/macros/add-items-checklist":{"title":"$:/plugins/kookma/kara/macros/add-items-checklist","type":"text/vnd.tiddlywiki","text":"\define kara-add-item-action()
<$let			
      item     = {{{ [<tempTid>get[text]] }}}
      newItem  = {{{ [<item>addprefix[ ]addprefix<undoneMark>] }}}
      newblock = {{{ [<dblock>addprefix<newItem>addprefix<lbr>] }}}
>
<$list filter="[<item>!is[blank]]" variable=null><!--ignore empty imput-->

	<$list filter="[<dblock0>is[blank]]"><!-- there is no todo block, create one -->
	<$action-setfield $tiddler=<<src>> text={{{ [<src>get[text]addsuffix<lbr>addsuffix<lbr>] [<dataBlockStartDelimiter>addsuffix<lbr>] [<newItem>addsuffix<lbr>] [<dataBlockEndDelimiter>] +[join[]] }}}/>
	</$list>

	<$vars in= {{{ [<dataBlockStartDelimiter>] [<dblock>]   [<dataBlockEndDelimiter>] +[join[]]}}}  
				 out={{{ [<dataBlockStartDelimiter>] [<newblock>] [<dataBlockEndDelimiter>] +[join[]]}}}  >
						<$action-setfield $tiddler=<<src>> text={{{ [<src>get[text]search-replace:g:<in>,<out>] }}}/>
	</$vars>
	<$action-deletetiddler $tiddler=<<tempTid>>/>
</$list>
</$let>	
\end"},"$:/plugins/kookma/kara/macros/add-items-interstitial":{"title":"$:/plugins/kookma/kara/macros/add-items-interstitial","type":"text/vnd.tiddlywiki","text":"\define kara-add-item-action()
<$let			
      note-time=<<now format:'0hh:0mm'>>
      item     = {{{ [<tempTid>get[text]] }}}
      newItem  = {{{ [[*]] [<note-time>addprefix['']addsuffix['']] [<item>] +[join[ ]]}}}
      newblock = {{{ [<dblock>addsuffix<newItem>addsuffix<lbr>] }}}
>
<$list filter="[<item>!is[blank]]" variable=null><!--ignore empty imput-->

<$list filter="[<dblock0>is[blank]]"><!-- there is no interstitial block, create one -->
<$action-setfield $tiddler=<<src>> text={{{ [<src>get[text]addsuffix<lbr>addsuffix<lbr>] [[@@.interstitial]addsuffix<lbr>] [<newItem>addsuffix<lbr>] [[@@]] +[join[]] }}}/>
</$list>

  <$vars in= {{{ [<dataBlockStartDelimiter>] [<dblock>]   [<dataBlockEndDelimiter>] +[join[]]}}}  
         out={{{ [<dataBlockStartDelimiter>] [<newblock>] [<dataBlockEndDelimiter>] +[join[]]}}}  >
    <$action-setfield $tiddler=<<src>> text={{{ [<src>get[text]search-replace:g:<in>,<out>] }}}/>
  </$vars>
<$action-deletetiddler $tiddler=<<tempTid>>/>
</$list>
</$let>	
\end"},"$:/plugins/kookma/kara/macros/add-items":{"title":"$:/plugins/kookma/kara/macros/add-items","type":"text/vnd.tiddlywiki","text":"<!-- this tiddler contains common macros for both checklist and interstitial -->

\define tempTid()   $:/temp/kara/add-item/$(tempId)$

\define kara-input-item()
<$keyboard key="escape" actions=<<kara-cancel-action>>   >
<$keyboard key="enter"  actions=<<kara-add-item-action>> >
<$edit-text 
 tiddler=<<tempTid>> 
 tag="input"
 class="kk-kara-input-textbox"
 placeholder=<<editTextPlaceholder>>
 default="" minHeight="1em" focus=<<focusStatus>>
 />
</$keyboard> 
</$keyboard> 
\end


\define kara-cancel-action()
<$action-deletetiddler $tiddler=<<tempTid>>/>
\end


\define kara-add-items()
<div class="kk-kara-header-ui">
<div class="kk-kara-header-textbox"><<kara-input-item>></div>
<div><$button class="tc-btn-invisible" actions=<<kara-add-item-action>> disabled={{{ [<tempTid>get[text]!is[blank]then[no]else[yes]] }}}>{{$:/core/images/new-button}}</$button></div>
</div>
\end"},"$:/plugins/kookma/kara/macros/checklist":{"title":"$:/plugins/kookma/kara/macros/checklist","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define checklist(tiddler, showInput, focus:"yes", mode:"")
\import $:/plugins/kookma/kara/macros/definitions
\import $:/plugins/kookma/kara/macros/add-items
\import $:/plugins/kookma/kara/macros/add-items-checklist
\import $:/plugins/kookma/kara/macros/disp-items-checklist
<$let tiddler={{{ [<__tiddler__>trim[]] }}}
      src= {{{ [<tiddler>!is[blank]then<tiddler>else<currentTiddler>] }}}
      dataBlockStartDelimiter ="@@.todo"
      dataBlockEndDelimiter   = "@@"  
      doneMark="[x]"
      undoneMark="[ ]"
      doneMarkPattern="^\[x\]"
      undoneMarkPattern="^\[\s?\]"
      tempId={{{ [<tiddler>!is[blank]then[checklist/$(currentTiddler)$/]addsuffix<tiddler>]
                 :else[[checklist/]addsuffix[$(currentTiddler)$]] }}}
      editTextPlaceholder="add new todo item"
      focusStatus=<<__focus__>>
      displayMode={{{ [<checklistDisplayConfig>get[text]] [<__mode__>] :and[first[]] }}}
      lbr="
"      >
<!-- extract the data block -->
<$let dblock0 = {{{ [<src>get[text]splitregexp:m[^@@\.todo]butfirst[1]] }}}
      dblock  = {{{ [<dblock0>splitregexp:m[^@@]butlast[1]] }}} >
<section class="kk-kara-ui">

<!-- display add items UI -->
<$list filter="[<__showInput__>!match[no]]" variable=null>
<<kara-add-items>>
</$list>

<!-- display checklist -->
<<kara-disp-items>>
</section>
</$let>
</$let>
\end

"},"$:/plugins/kookma/kara/macros/definitions":{"title":"$:/plugins/kookma/kara/macros/definitions","code-body":"yes","tags":"","type":"text/vnd.tiddlywiki","text":"\define checklistDisplayConfig() $:/config/kara/checklist/mode
\define interstitialDisplayConfig() $:/config/kara/interstitial/mode"},"$:/plugins/kookma/kara/macros/disp-items-checklist":{"title":"$:/plugins/kookma/kara/macros/disp-items-checklist","type":"text/vnd.tiddlywiki","text":"\define toggle-action(param)
<!-- toggle a line done/undone. the lbr is used to prevent replace "[x] Hi" with "[x] Hi Jeremy"-->
<$let param={{{[<__param__>match[done]then<doneMark>else<undoneMark>]}}} 
      in={{{ [<row>addsuffix<lbr>] }}}
      out={{{ [<entry>trim[]addprefix[ ]addprefix<param>addsuffix<lbr>] }}}
   >
     <$action-setfield $tiddler=<<src>> text={{{ [<src>get[text]search-replace:g:<in>,<out>] }}}/>
</$let>
\end

\define disp-single-item()
<!-- show a task item -->
<$list filter="[<row>regexp<undoneMarkPattern>splitregexp<undoneMarkPattern>butfirst[]]" variable=entry><!-- butfirst ignores first empty output-->
	<$button class="tc-btn-invisible" actions="""<<toggle-action "done">>""" ><input type=checkbox/></$button>&nbsp;<$transclude tiddler=<<entry>> field=title/>
</$list>
<!-- show a done item -->
<$list filter="[<row>regexp<doneMarkPattern>splitregexp<doneMarkPattern>butfirst[]]" variable=entry><!-- butfirst ignores first empty output-->
	<$button class="tc-btn-invisible" actions="""<<toggle-action "undone">>""" ><input type=checkbox checked/></$button>&nbsp;<span class="kk-kara-done-item"><$transclude tiddler=<<entry>> field=title/></span>
</$list>
\end

\define delete-single-item()
<$button class="tc-btn-invisible">{{$:/plugins/kookma/kara/images/times}}
  <$vars in={{{ [<row>addsuffix<lbr>] }}} out="" }}} >
    <$action-setfield $tiddler=<<src>> text={{{ [<src>get[text]search-replace:g:<in>,<out>] }}}/>
  </$vars>
</$button>
\end



\define kara-disp-items()
<$let defaultDispFilter= "[<dblock>splitregexp[\n]!is[blank]]"
      sortedDispFilter=  "[<dblock>splitregexp[\n]!is[blank]trim:prefix[]sort[]]"
      dispFilter={{{ [<displayMode>match[sort]then<sortedDispFilter>else<defaultDispFilter>]}}}
      itemPattern={{{ [<doneMarkPattern>] [<undoneMarkPattern>] +[join[|]] }}}
>			

<ul class="kk-kara-checklist">
<$list filter="[subfilter<dispFilter>trim[]] :filter[regexp<itemPattern>]" variable=row>
<li class="kk-kara-row">
<div class="kk-kara-desc"><<disp-single-item>></div>
<div class="kk-kara-delete"><<delete-single-item>></div>
</li>
</$list>
</ul>

</$let>
\end

<!-- checklist items can be shown as entered irrespective of their status (completed: done, or incompleted)
and they can be separately: incomplete/tasks first, completed tasks (done) last.
The display format is determined using a global config, and mode input parameter. global config has higher priority
-->"},"$:/plugins/kookma/kara/macros/disp-items-interstitial":{"title":"$:/plugins/kookma/kara/macros/disp-items-interstitial","tags":"","type":"text/vnd.tiddlywiki","text":"\define kara-disp-items()
<div class="interstitial" style="display:block;">
<$list filter="[<displayMode>regexp[reverse|sort]]" emptyMessage='<$transclude tiddler=<<dblock>> field=title mode=block/>' variable=ignore>
	<$list filter="[<displayMode>match[reverse]]" variable=ignore>
		<$transclude tiddler={{{ [<dblock>splitregexp[\n]!is[blank]splitregexp[\s*\*\s*]!sort[]addprefix[* ]] :and[join<lbr>] }}} field=title mode=block/>
	</$list>
	<$list filter="[<displayMode>match[sort]]" variable=ignore>
		<$transclude tiddler={{{ [<dblock>splitregexp[\n]!is[blank]splitregexp[\s*\*\s*]sort[]addprefix[* ]] :and[join<lbr>] }}} field=title mode=block/>
	</$list>
</$list>
</div>
\end



<!--
the order param is used to control the chornological order of notes
default is as they are added, the reverse is, latest first.
Defensive programming: the trim spaces, and star (*) and adding the star with space (* ) is to handle manually added notes
where, user may add extra spaces before or after start(*)
-->"},"$:/plugins/kookma/kara/macros/interstitial":{"title":"$:/plugins/kookma/kara/macros/interstitial","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define interstitial(tiddler, showInput, focus:"yes", mode:"")
\import $:/plugins/kookma/kara/macros/definitions
\import $:/plugins/kookma/kara/macros/add-items
\import $:/plugins/kookma/kara/macros/add-items-interstitial
\import $:/plugins/kookma/kara/macros/disp-items-interstitial
<$let tiddler={{{ [<__tiddler__>trim[]] }}}
      src= {{{ [<tiddler>!is[blank]then<tiddler>else<currentTiddler>] }}}
      dataBlockStartDelimiter ="@@.interstitial"
      dataBlockEndDelimiter   = "@@"  
      tempId={{{ [<tiddler>!is[blank]then[interstitial/$(currentTiddler)$/]addsuffix<tiddler>]
                 :else[[interstitial/]addsuffix[$(currentTiddler)$]] }}}			
      editTextPlaceholder="add new interstitial note"
      focusStatus=<<__focus__>>
      displayMode={{{ [<interstitialDisplayConfig>get[text]] [<__mode__>] :and[first[]] }}}
      lbr="
"      >
<!-- extract the data block -->
<$let dblock0 = {{{ [<src>get[text]splitregexp:m[^@@\.interstitial]butfirst[1]] }}}
      dblock  = {{{ [<dblock0>splitregexp:m[^@@]butlast[1]] }}} >
<section class="kk-kara-ui">		

<!-- display add items UI -->
<$list filter="[<__showInput__>!match[no]]" variable=null>
<<kara-add-items>>
</$list>

<!-- display interstitial notes -->
<<kara-disp-items>>
</section>
</$let>
</$let>
\end
"},"$:/plugins/kookma/kara/macros/list-all-items":{"title":"$:/plugins/kookma/kara/macros/list-all-items","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define list-all-items(filter:"[!is[system]]", type:"", showInput:"no", mode:"")
\define dt-arrow() <svg viewBox="0 0 20 20" class=""><path d="M12.95 10.707l.707-.707L8 4.343 6.586 5.757 10.828 10l-4.242 4.243L8 15.657l4.95-4.95z" /></svg>
<$list filter="""$filter$  :and[search:text[@@.$type$]] :filter[{!!text}splitregexp[\n]trim[]!is[blank]regexp:title[^@@.$type$]]""">
<details class="kk-kara-dt" open>
<summary><$link/><$list filter="[<__type__>match[todo]]" variable=null> | <$view field=modified format=date template="DDth mmm YYYY at 0hh12:0mmam"/></$list><<dt-arrow>></summary>
<$list filter="[<__type__>match[todo]then[checklist]] [<__type__>match[interstitial]then[interstitial]] :and[first[]]" variable=macName>
<blockquote><$macrocall $name=<<macName>> showInput=<<__showInput__>> mode=<<__mode__>>/></blockquote>
</$list>
</details>
\end"},"$:/plugins/kookma/kara/readme":{"title":"$:/plugins/kookma/kara/readme","tags":"","type":"text/vnd.tiddlywiki","text":"; Kara
Kara is a small in tiddler plain checklist and interstitial journaling. It has a minimal UI and lets you manually add/remove items/notes to your checklist/interstitial journal. Items can be simply searched using standard search. Kara does not use any system or third party tiddlers.

;Code and demo
For learning plugin features, syntax, tutorial and examples see the plugin demo and code pages

* Demo: https://kookma.github.io/TW-Kara/
* Code: https://github.com/kookma/TW-Kara
"},"$:/plugins/kookma/kara/styles/checklist":{"title":"$:/plugins/kookma/kara/styles/checklist","tags":"$:/tags/Stylesheet","type":"text/vnd.tiddlywiki","text":"ul.kk-kara-checklist{
	list-style: none;
	padding:0;
}

/* Kara items ui */
.kk-kara-row{
	display: flex;	
	width: 100%; /* for larg screen width> 960px*/
	flex-wrap: nowrap;
	border-radius:5px;
}

.kk-kara-row:hover {
	/* background-color: #f6f6f6;*/
	background-color: <<colour table-header-background>>;
}

.kk-kara-row .kk-kara-delete {
	flex-grow:0;
	width:15px;
}

.kk-kara-row .kk-kara-delete {
	opacity: 0.3;
}

.kk-kara-row:hover .kk-kara-delete {
	opacity: 1;
}

.kk-kara-row .kk-kara-desc{
	flex-grow:1; 
	width: calc(100% - 50px); 
	padding-right: 10px;
}


.kk-kara-done-item{
	font-style:oblique;
	opacity:0.5;
	/*	text-decoration: line-through;*/
	text-decoration: line-through <<colour alert-highlight>>;
}"},"$:/plugins/kookma/kara/styles/details":{"title":"$:/plugins/kookma/kara/styles/details","text":"details.kk-kara-dt  > summary {
	list-style: none;
	padding-left:5px;

}

details.kk-kara-dt   summary {
	cursor: pointer;
	font-weight:300;
}

details.kk-kara-dt   > summary::-webkit-details-marker {
	display: none;
}
details.kk-kara-dt   {
	user-select: none;
}

details.kk-kara-dt[open] summary svg {
	transform: rotate(+90deg);
}

details.kk-kara-dt   summary svg {
	transition: all 0.3s;
	width: 1.2em;
	height: 1.2em;
	vertical-align: baseline;
	float:right;
}

details.kk-kara-dt:focus {
	outline: none;
}

/* hide details arrow by default, show it on hover */
details.kk-kara-dt summary svg{
	opacity:0;
  transition: opacity 0.6s ease-in;
}
details.kk-kara-dt:hover summary  svg{
	opacity:1;
}","tags":"$:/tags/Stylesheet","type":"text/css"},"$:/plugins/kookma/kara/styles/header-ui":{"title":"$:/plugins/kookma/kara/styles/header-ui","text":".kk-kara-header-ui{
	display: flex;
	width: 100%;
}
.kk-kara-header-ui > div{
	margin: 2px;
	flex-grow:0;	
}
.kk-kara-header-ui .kk-kara-header-textbox{
	flex-grow:1;	
}

/* inputbox for add-task */
.kk-kara-input-textbox {
	width:100%;
	/*padding-left: 5px;*/
	border: none;
	border-bottom: 1px dotted grey;
}

.kk-kara-input-textbox:focus {
	outline: none;
	border-bottom: 1px solid #5778d8;
	background: transparent;
}

/* for performance reason and loss of focus, the add-item ui has been hidden in preview mode */
.tc-tiddler-preview-preview .kk-kara-header-ui {
	display:none;
}
