\function answer.tiddlers()
[all[current]has[draft.of]get[draft.of]removeprefix[Q]addprefix[A]]
:else[all[current]removeprefix[Q]addprefix[A]]
:map:flat[all[tiddlers+shadows]prefix<currentTiddler>]
\end

\function question.tiddler()
[all[current]has[draft.of]get[draft.of]removeprefix[A]addprefix[Q]]
:else[all[current]removeprefix[A]addprefix[Q]]
:and[search-replace:g:regexp<multi-answer-pattern>,[]trim[]]
\end

\procedure create-answer-tiddler()
<$button class="tc-btn-invisible tc-tiddlylink">  
  <$action-sendmessage $message="tm-new-tiddler"  
    title=<<target.tiddler>>  
    tags="Answer"/>  
  	<<target.tiddler>>  
</$button>  
\end

<% if [all[current]tag[Question]] %>
	<%if [target.tiddler[]is[missing]] %>
		Create answer: <<create-answer-tiddler>>
	<%else%>
		Go to answer: <$list filter="[answer.tiddlers[]]" join=" | "/>
	<%endif%>	
<% elseif [all[current]tag[Answer]] %>
  Go to question: <$link to=<<question.tiddler>>/>
<% endif %>"},"$:/plugins/kookma/pdeck/viewtemplates/question":{"title":"$:/plugins/kookma/pdeck/viewtemplates/question","created":"20250515044532759","list-before":"$:/core/ui/ViewTemplate/body","modified":"20250703114102951","tags":"$:/tags/ViewTemplate","type":"text/vnd.tiddlywiki","text":"\function question.fields() exam source topic
\procedure showField()
<%if [{!!title}has<field>] %>
	<tr>
		<th><$text text=<<field>>/></th>
		<%if [<field>!match[source]] %>
			<td><$list filter="[<currentTiddler>get<field>enlist-input[]]" join=" | "><$link/></$list></td>
		<%else%><!-- display the source field, as it will be used with Refnotes plugin -->
			<td><$transclude $field=<<field>>/></td>
		<%endif%>
	</tr>
<%endif%>
\end

<$list filter="[<currentTiddler>tag[Question]]">
	<table class="table-borderless kk-pdeck-question-info">
		<$list filter=[question.fields[]] variable=field><<showField>></$list>
  </table>
</$list>

