.py-btn {
  border-radius: 1.5em;
  border: 1px solid #ccc;
  background: <<colour tiddler-background>>;
  padding: .3em 1em;
  cursor: pointer;
  transition: box-shadow ease-in .1s;
}

.py-btn:focus {
  outline: 0 none;
}

.py-btn:active {
  box-shadow: 0 0 0 0.2rem rgba(0,0,0,.08);
}

.py-btn.btn-small {
  font-size: .9em;
  padding: .2em .8em;
}

