<<<"},"$:/plugins/kookma/section/macros/definition":{"title":"$:/plugins/kookma/section/macros/definition","tags":"$:/tags/Section/Macro","type":"text/vnd.tiddlywiki","text":"\define se-styleTid()    $:/config/section-editor/style-in-use
\define se-hlevelTid()   $:/config/section-editor/hlevel

\define se-config-visibility-toolbar() $:/config/section-editor/config-visibility-toolbar
\define se-editor()  $:/config/section-editor/config-editor-type

\define se-readermodeTid() $:/config/section-editor/reader-mode

<!-- patchwork tiddler setting -->
\define se-patchworkheaderTid() $:/config/section-editor/patchwork-header-mode
\define se-patchworkfooterTid() $:/config/section-editor/patchwork-footer-mode


<!-- state and temporary tiddlers -->
\define stateTid() $:/section-editor/state/$(source)$
\define editTid()  $:/section-editor/content/$(source)$/$(counter)$ 
\define foldstatusTid()  $:/temp/section-editor/foldstate/$(source)$/$(counter)$ "},"$:/plugins/kookma/section/macros/main":{"title":"$:/plugins/kookma/section/macros/main","code-body":"yes","tags":"","type":"text/vnd.tiddlywiki","text":"\define pattern() \n\s*(?=!{1,$(hn)$}[^!])

\define sectionizeSingleTiddler(sourceTiddler)
\import $:/plugins/kookma/section/macros/definition
<$vars hn={{{ [<se-hlevelTid>get[text]else[2]] }}} > <!-- default level of headings 2 -->
<$macrocall $name=sectionizer source=<<__sourceTiddler__>> pattern=<<pattern>> />
</$vars>
\end


\define sectionizePatchworkTiddler(sourceTiddler)
<$vars patchfilter={{{ [<__sourceTiddler__>get[se-type]]  }}} >
<$list filter="[subfilter<patchfilter>]">
{{||$:/plugins/kookma/section/templates/tiddler-header}}
<$macrocall $name=sectionizeSingleTiddler sourceTiddler=<<currentTiddler>> />
{{||$:/plugins/kookma/section/templates/tiddler-footer}}
</$list>
</$vars>
\end
"},"$:/plugins/kookma/section/macros/marker":{"title":"$:/plugins/kookma/section/macros/marker","tags":"$:/tags/Section/Macro","type":"text/vnd.tiddlywiki","text":"\define details-marker()
<$vars style-in-use={{{ [<se-styleTid>get[text]else[$:/plugins/kookma/section/styles/themes/basic]] }}} >
<$vars marker-open={{{   [<style-in-use>get[marker-open]]   ~[[$:/core/images/down-arrow]]  }}}
       marker-closed={{{ [<style-in-use>get[marker-closed]] ~[[$:/core/images/right-arrow]] }}} >
<$transclude tiddler={{{ [<foldstatusTid>get[text]match[closed]then<marker-closed>else<marker-open>]  }}} mode=inline />
</$vars>
</$vars>
\end

<!--
$:/core/images/right-arrow
$:/core/images/down-arrow
-->"},"$:/plugins/kookma/section/macros/sectionizer":{"title":"$:/plugins/kookma/section/macros/sectionizer","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define sectionizer(source, pattern:"")
\import [all[tiddlers+shadows]tag[$:/tags/Section/Macro]]
<$vars source=<<__source__>> 
       sourceText={{{[<__source__>get[text]]}}} 
       pattern=<<__pattern__>> nonWhitespace="[^\s]" lbr="""
"""><article class="se-article">
<$list counter=counter variable=currentSection
       filter="[<sourceText>splitregexp<pattern>!is[blank]regexp<nonWhitespace>]">
<$set name=seTemplate filter="[<currentSection>trim:prefix[]prefix[!]]" 
      value="$:/plugins/kookma/section/templates/section" 
      emptyValue="$:/plugins/kookma/section/templates/foreword">
  <$transclude tiddler=<<seTemplate>> mode=inline/>
</$set>
</$list>
</article></$vars>
\end"},"$:/plugins/kookma/section/readme":{"title":"$:/plugins/kookma/section/readme","tags":"","type":"text/vnd.tiddlywiki","text":"; Section Editor plugin
Sectionize big tiddlers on headings, edit in place, create, fold and manage sections.

Create patchwork tiddlers, to weave the fragments together to present narrative stories.

; Code and demo
For learning plugin features, syntax, tutorial and examples see the plugin demo and code pages

* Demo: https://kookma.github.io/TW-Section/
* Code: https://github.com/kookma/TW-Section
"},"$:/plugins/kookma/section/styles/disclosure":{"title":"$:/plugins/kookma/section/styles/disclosure","tags":"$:/tags/Stylesheet","type":"text/vnd.tiddlywiki","text":"\import $:/plugins/kookma/section/macros/definition

<$list filter="section section-header section-body section-toolbar editor tiddler-header-footer +[addprefix[$:/plugins/kookma/section/styles/]]"><$transclude/></$list>

<$transclude tiddler={{{ [<se-styleTid>get[text]else[$:/plugins/kookma/section/styles/themes/basic]] }}} field=text mode=block/>

<$list filter="[<se-readermodeTid>get[text]match[yes]]" variale=null>
<$transclude tiddler="$:/plugins/kookma/section/styles/reader" mode=block/>
</$list>

<$list filter="[<se-patchworkheaderTid>get[text]match[yes]]" variale=null>
<$transclude tiddler="$:/plugins/kookma/section/styles/tiddler-header" mode=block/>
</$list>

<$list filter="[<se-patchworkfooterTid>get[text]match[yes]]" variale=null>
<$transclude tiddler="$:/plugins/kookma/section/styles/tiddler-footer" mode=block/>
</$list>"},"$:/plugins/kookma/section/styles/editor":{"title":"$:/plugins/kookma/section/styles/editor","text":".se-simple-textarea{
	/*margin-top: 6px; */
	padding: 6px;

	width:100%;
	min-height: 40px;
	resize: none; /* vertical user can resize vertically, but width is fixed */
}

/* for main editor when appears in the sidebar */
.se-section-edit .tc-edit-texteditor { width:100%; }

/* main editor toolbar settings for dynamic table */
.se-section-edit .tc-editor-toolbar button svg{
	height:0.9em;  /* make toolbar buttons smaller */
	width:0.9em
}

/* make third party plugins like KaTeX button smaller */
.se-section-edit  .tc-editor-toolbar button .katex{
	font-size: 0.9em;
	line-height:1em;
}","tags":"","type":"text/css"},"$:/plugins/kookma/section/styles/reader":{"title":"$:/plugins/kookma/section/styles/reader","text":".se-section-toolbar{display:none;}
","tags":"","type":"text/css"},"$:/plugins/kookma/section/styles/section-body":{"title":"$:/plugins/kookma/section/styles/section-body","tags":"$:/tags/Stylesheet","type":"text/vnd.tiddlywiki","text":"\import $:/plugins/kookma/section/macros/definition

.se-sectionbody {
	/* set distance to edit/done/cancel buttons*/
/*	margin-top:4.5rem;  */
}

/* show/hide main editor toolbar */
<$list filter="[<se-config-visibility-toolbar>get[text]!match[yes]]">
.se-tc-tiddler-body .tc-editor-toolbar{
display:none;
}
</$list>

/* reserved for future 
.se-section-view{border:1px dotted purple;} <!--todo class -->
.se-section-edit{border:1px dotted green;} <!--todo class -->
*/
"},"$:/plugins/kookma/section/styles/section-header":{"title":"$:/plugins/kookma/section/styles/section-header","text":".se-sectionheader button{ 
	width:100%;
	display: inline-flex;
	align-items: center;
	justify-content:space-between;
}

.se-sectionheader button svg {
	/*for future use */
	flex-shrink:0; /*keep svg size constant and not shrink*/
}

/* ? may be modified latter */
.se-sectionheader button h1,
.se-sectionheader button h2,
.se-sectionheader button h3,
.se-sectionheader button h4,
.se-sectionheader button h5
{
	margin: 0;
	padding:0;	
	text-align:left; /* for very long heading*/
}
","tags":"","type":"text/css"},"$:/plugins/kookma/section/styles/section-toolbar":{"title":"$:/plugins/kookma/section/styles/section-toolbar","text":".se-section-toolbar {
	/* set distance to edit/done/cancel buttons*/
	width:100%;
	display: inline-flex;
	align-items: center;
	justify-content:flex-end;
}

.se-section-toolbar button{
	opacity:0;  /* autohide the edit button on section */
	margin-left:6px;
}
.se-section:hover .se-section-toolbar button{
	opacity:1;
}","tags":"","type":"text/css"},"$:/plugins/kookma/section/styles/section":{"title":"$:/plugins/kookma/section/styles/section","text":".se-section{ /*wrapp a single section*/
	padding:8px;
	margin-bottom:5px;
} 
","tags":"","type":"text/css"},"$:/plugins/kookma/section/styles/themes/basic":{"title":"$:/plugins/kookma/section/styles/themes/basic","text":"/*
.se-article{}
.se-section{}
.se-sectionheader{}
.se-sectionheader button{}
.se-sectionheader button svg{}

.se-section-toolbar {}
.se-section-toolbar button{}

/* edit-done-close(cancel)
.se-section-toolbar .tc-image-button{}
.se-section-toolbar .tc-image-edit-button{}
.se-section-toolbar .tc-image-done-button {}
.se-section-toolbar .tc-image-close-button{}


.se-sectionbody{}
.se-sectionbody .se-section-view{}
.se-sectionbody .se-section-edit{}
*/

.se-sectionheader button{
	border-bottom:1px solid #ccc;
	padding-bottom: 3px;
}
","caption":"Basic style","marker-closed":"","marker-open":"","tags":"$:/tags/Section/Theme","type":"text/css"},"$:/plugins/kookma/section/styles/themes/Divided":{"title":"$:/plugins/kookma/section/styles/themes/Divided","text":"/*
.se-section{}
.se-sectionheader{}
.se-sectionheader button{}
.se-sectionheader button svg{}

.se-section-toolbar {}
.se-section-toolbar button{}

.se-sectionbody{}
.se-sectionbody .se-section-view{}
.se-sectionbody .se-section-edit{}
*/

.se-sectionheader button{
	border-top:1px solid #ccc;
	fill: #aaa;
	padding-top:5px;
}

.se-sectionheader button h1,
.se-sectionheader button h2,
.se-sectionheader button h3,
.se-sectionheader button h4,
.se-sectionheader button h5 {
    font-size: 1.5em;
    font-weight: 300;
}","caption":"Divided style","marker-closed":"$:/core/images/new-button","marker-open":"$:/core/images/close-button","tags":"$:/tags/Section/Theme","type":"text/css"},"$:/plugins/kookma/section/styles/themes/faq":{"title":"$:/plugins/kookma/section/styles/themes/faq","text":"/*
.se-section{}
.se-sectionheader{}
.se-sectionheader button{}
.se-sectionheader button svg{}

.se-sectionbody{}
.se-sectionbody .se-section-view{}
.se-sectionbody .se-section-edit{}
*/

.se-sectionheader button{ 
	padding-bottom: 8px;
	justify-content: flex-end;
	flex-direction: row-reverse;
}
.se-sectionheader button svg{
	margin-right:8px;
	width:1.4em;
	height:1.4em;
	fill: #e31b1b; 
}


.se-section {
	border-bottom: 0.07em solid #ededed;   /* has color (1)*/
	padding: 1em 0em;
} 

.se-section:last-child {
	border: initial;
}


.se-sectionheader button h1,
.se-sectionheader button h2,
.se-sectionheader button h3,
.se-sectionheader button h4,
.se-sectionheader button h5 {
	font-size: 1.4em;
	font-weight: 500;
}

.se-section .se-sectionbody {
	border-left: 2px solid #e31b1b;    /* has color (2)*/
	margin-left: 40px;
	padding-left: 25px;
	opacity: 100;
	padding-bottom:20px;
}

.se-sectionheader:hover button {
    color: #e31b1b;
}","caption":"FAQ style","marker-closed":"$:/plugins/kookma/section/images/plus-square","marker-open":"$:/plugins/kookma/section/images/minus-square","tags":"$:/tags/Section/Theme","type":"text/css"},"$:/plugins/kookma/section/styles/themes/green-box":{"title":"$:/plugins/kookma/section/styles/themes/green-box","text":".se-section{
    background: hsla(153, 48%, 49%, .1);
    border: 1px solid hsla(162, 76%, 32%, .3);
    /* color: hsl(162, 76%, 32%);*/
    border-radius: 5px;
}

.se-section-toolbar button svg {
	/*for future use */
	fill:hsl(162, 76%, 32%);
}

.se-sectionheader button{ 
    color: hsl(162, 76%, 32%);
    font-weight: bold;

}

.se-sectionheader button svg {
	/*for future use */
	fill:hsl(162, 76%, 32%);
}


","caption":"Green box","tags":"$:/tags/Section/Theme","type":"text/css"},"$:/plugins/kookma/section/styles/themes/numbered":{"title":"$:/plugins/kookma/section/styles/themes/numbered","text":".se-section {
	border-bottom: 0.07em solid #ededed; 
	padding: 0.25em 1em;
} 

.se-section:last-child {
	border: initial;
}

.se-sectionheader button{ 
	padding-bottom: 0px;
	flex-direction: row;

	justify-content: flex-start;

}

.se-sectionheader button svg{
	display:none;
}

.se-sectionbody{
	margin-left:25px;
	margin-top:0px;
}


/* add number to section */

.se-tc-tiddler-body {
	counter-reset: section;  /* Set a counter named 'section', and its initial value is 0. */
}

.se-sectionheader button::before {
	counter-increment: section;      /* Increment the value of section counter by 1 */
	content: counter(section, decimal-leading-zero) " ";
	font-weight:bold;
	font-size:1.25em;
	color:lightsalmon;  /* lightsalmon, #e31b1b */
	margin-right:25px;
	margin-left:-10px;
}


","caption":"Numbered style","tags":"$:/tags/Section/Theme","type":"text/css"},"$:/plugins/kookma/section/styles/themes/siah":{"title":"$:/plugins/kookma/section/styles/themes/siah","text":"/*
.se-section{}
.se-sectionheader{}
.se-sectionheader button{}
.se-sectionheader button svg{}

.se-sectionbody{}
.se-sectionbody .se-section-view{}
.se-sectionbody .se-section-edit{}
*/


.se-sectionheader button{ 
	border-bottom:1px solid #aaa;
	padding-bottom:3px;
	justify-content: flex-end;
	flex-direction: row-reverse;
}
.se-sectionheader button svg{
	margin-right:8px;
	width:1.1em;
	height:1.1em;
}
.se-sectionheader button h1,
.se-sectionheader button h2,
.se-sectionheader button h3 {
	font-weight:bold;
	font-size:1.25em;	
}","caption":"Siah style","marker-closed":"$:/plugins/kookma/section/images/plus-solid","marker-open":"$:/plugins/kookma/section/images/minus-solid","tags":"$:/tags/Section/Theme","type":"text/css"},"$:/plugins/kookma/section/styles/themes/simple-box":{"title":"$:/plugins/kookma/section/styles/themes/simple-box","text":".se-section{
	border: 1px solid #aaa;
	border-radius: 5px;
}

.se-sectionheader button{}
.se-sectionheader button svg{}

.se-sectionbody{
	border-top: 1px solid #aaa;
	margin-left:  -8px;
	margin-right: -8px; /* set from .se-section */
	margin-top:4px;
	padding:8px;
}","caption":"Simple box","tags":"$:/tags/Section/Theme","type":"text/css"},"$:/plugins/kookma/section/styles/tiddler-body":{"title":"$:/plugins/kookma/section/styles/tiddler-body","tags":"$:/tags/Stylesheet","type":"text/vnd.tiddlywiki","text":"\rules only filteredtranscludeinline transcludeinline

/* set the font size like TW tc-tiddler-body */
.se-tc-tiddler-body {
	font-size:   {{$:/themes/tiddlywiki/vanilla/metrics/bodyfontsize}};
	line-height: {{$:/themes/tiddlywiki/vanilla/metrics/bodylineheight}};
}

/*
.se-tc-tiddler-body is equal to .tc-tiddler-body for non sectionized tiddlers.

*/"},"$:/plugins/kookma/section/styles/tiddler-footer":{"title":"$:/plugins/kookma/section/styles/tiddler-footer","text":".se-tiddler-footer{display:none;}
","tags":"","type":"text/css"},"$:/plugins/kookma/section/styles/tiddler-header-footer":{"title":"$:/plugins/kookma/section/styles/tiddler-header-footer","text":".se-tiddler-header,
.se-tiddler-footer {
	/* set distance to edit/done/cancel buttons*/
	width:100%;
	display: inline-flex;
	flex-wrap: wrap;
	align-items: center;
	font-size: 0.8em;
	font-weight: 300;
	padding-left:8px;
	padding-right:8px;
}
.se-tiddler-header span,
.se-tiddler-footer span{
	display: inline-block;
	margin:0 3px;
	border:1px solid #ccc;
	border-radius:0.8em;
	padding: 0.16em 0.7em;
	line-height: 1.2em;
	white-space: nowrap;
}

.se-tiddler-header { justify-content:flex-start;}
.se-tiddler-footer { justify-content:flex-end; }


.se-tiddler-footer span,
.se-tiddler-footer span a{
	background-color: #4d4d4d;
	color:white;
}
","tags":"","type":"text/css"},"$:/plugins/kookma/section/styles/tiddler-header":{"title":"$:/plugins/kookma/section/styles/tiddler-header","text":".se-tiddler-header{display:none;}
","tags":"","type":"text/css"},"$:/plugins/kookma/section/templates/edit":{"title":"$:/plugins/kookma/section/templates/edit","code-body":"yes","tags":"","type":"text/vnd.tiddlywiki","text":"<$keyboard actions=<<doneActions>> key="ctrl+enter">
<$keyboard actions=<<doneActions>> key="meta+enter"><!-- support MacOS -->
<$keyboard actions=<<cancelActions>> key="escape">
<$reveal type="nomatch" state=<<se-editor>> text="main-editor" default="simple-textarea">
<$edit-text tiddler=<<editTid>> field="text" tag=textarea class="se-simple-textarea"/>
</$reveal>
<$reveal type="match" state=<<se-editor>> text="main-editor" default="simple-textarea">
<$list filter="[<editTid>]" template="$:/core/ui/EditTemplate/body"/>
</$reveal>
</$keyboard>
</$keyboard>
</$keyboard>"},"$:/plugins/kookma/section/templates/foreword":{"title":"$:/plugins/kookma/section/templates/foreword","code-body":"yes","tags":"","type":"text/vnd.tiddlywiki","text":"<!-- display foreword without sectionizing -->
<$transclude tiddler=<<currentSection>> field=title mode="block"/>"},"$:/plugins/kookma/section/templates/section-body":{"title":"$:/plugins/kookma/section/templates/section-body","code-body":"yes","tags":"todo","type":"text/vnd.tiddlywiki","text":"\define editActions()
<!-- action when edit button is clicked -->
<$action-setfield $tiddler=<<stateTid>> $index=<<counter>> $value="edit" />
<$action-setfield $tiddler=<<editTid>> $field=text $value=<<currentSection>> throttle.refresh=""/>
\end

\define xxdoneActions()
<!-- NOT WORKING: keep it and use when GitHub issue resolved -->
<!-- action when done button is clicked -->
<$action-setfield $tiddler=<<stateTid>> $index=<<counter>> $value="view" />
<$vars for=<<currentSection>> with={{{[<editTid>get[text]]}}}>
<$action-setfield $tiddler=<<source>> $field=text $value={{{[<source>get[text]search-replace::regexp<for>,<with>]}}} />
<$action-deletetiddler $tiddler=<<editTid>> />
</$vars>
\end

\define doneActions()
<!-- action when done button is clicked -->
<$action-setfield $tiddler=<<stateTid>> $index=<<counter>> $value="view" />
<$vars for=<<currentSection>> with={{{[<editTid>get[text]]}}}>
<$action-setfield $tiddler=<<source>> $field=text $value={{{[<source>get[text]split<for>join<with>]}}} />
<$action-deletetiddler $tiddler=<<editTid>> />
