      {{$:/core/ui/SideBarSegments/page-controls}}
    </div>
  </div>
</$reveal>

"},"$:/plugins/mabuqian/xnote/ui/Buttons/PageControls":{"title":"$:/plugins/mabuqian/xnote/ui/Buttons/PageControls","tags":"$:/tags/PageControls","caption":"{{$:/plugins/mabuqian/xnote/images/bars}} Sidebar open/close button","description":"Sidebar open/close button","type":"text/vnd.tiddlywiki","text":"<$reveal state="$:/state/notebook-sidebar" type="match" text="yes" default="no" retain="yes" animate="no">
  <$button set="$:/state/notebook-sidebar" setTo="no" tooltip="Toggle menu" class="tc-btn-invisible">
    {{$:/plugins/mabuqian/xnote/images/bars}}
  </$button>
</$reveal>

<$reveal type="nomatch" state="$:/state/notebook-sidebar" text="yes">
  <$button set="$:/state/notebook-sidebar" setTo="yes" tooltip="Toggle menu" class="tc-btn-invisible ">
    {{$:/plugins/mabuqian/xnote/images/bars}}
  </$button>
</$reveal>
"},"$:/plugins/mabuqian/xnote/ui/Buttons/SwitchPalette":{"title":"$:/plugins/mabuqian/xnote/ui/Buttons/SwitchPalette","created":"20201210171047824","description":"Toggle between grey/beige colour palette","modified":"20210118213335643","tags":"$:/tags/PageControls","type":"text/vnd.tiddlywiki","text":"<span class="desktop-only">
  <$vars
    palettes="[all[tiddlers+shadows]tag[$:/tags/notebook/Palette]]"
    popupTiddler=<<qualify "$:/state/notebook/palette-dropdown">>
  >
    <$button
      popup=<<popupTiddler>>
      tooltip="Switch colours"
      aria-label="Switch colours"
      class=<<tv-config-toolbar-class>>
    >
      <$list filter="[<tv-config-toolbar-icons>match[yes]]">
	{{$:/plugins/mabuqian/xnote/images/color-switch}}
      </$list>

      <$list filter="[<tv-config-toolbar-text>match[yes]]">
	<span class="tc-btn-text">Switch colours</span>
      </$list>

      <$reveal state=<<popupTiddler>> type="popup" position="belowleft" class="tc-drop-down">
	<$list filter=<<palettes>>>
	  <$button class="tc-btn-invisible">
	    {{!!name}}
	    <$action-setfield $tiddler="$:/palette" text={{!!title}}/>
	  </$button>
	</$list>
      </$reveal>

    </$button>
  </$vars>
</span>
"},"$:/plugins/mabuqian/xnote/ui/Buttons/toolbar":{"title":"$:/plugins/mabuqian/xnote/ui/Buttons/toolbar","tags":"$:/tags/ViewToolbar","caption":"{{$:/plugins/mabuqian/xnote/images/bars}} Sidebar open/close button","description":"Sidebar open/close button","type":"text/vnd.tiddlywiki","text":"\whitespace trim
<%if [{$:/state/notebook-sidebar}match[yes]] %>
  <$button set="$:/state/notebook-sidebar" setTo="no"  class=<<tv-config-toolbar-class>> selectedClass="tc-selected" tooltip="Toggle menu" aria-label="Toggle menu"> 
    <$list filter="[<tv-config-toolbar-icons>match[yes]]">
{{$:/plugins/mabuqian/xnote/images/bars}}
    </$list>
	<$list filter="[<tv-config-toolbar-text>match[yes]]">
        <span class="tc-btn-text">
				Toggle menu
				 </span>
				 </$list>
</$button>
<%else%>
  <$button set="$:/state/notebook-sidebar" setTo="yes" tooltip="Toggle menu" class=<<tv-config-toolbar-class>> >
   <$list filter="[<tv-config-toolbar-icons>match[yes]]">
<span style="fill: red;">{{$:/plugins/mabuqian/xnote/images/bars}}</span>
  </$list>
	<$list filter="[<tv-config-toolbar-text>match[yes]]">
        <span class="tc-btn-text">
				Toggle menu
				 </span>
	</$list>
  </$button>
<%endif%>

"},"$:/plugins/mabuqian/xnote/ui/Buttons/menu":{"title":"$:/plugins/mabuqian/xnote/ui/Buttons/menu","created":"20200429115248943","modified":"20210124211756417","tags":"","type":"text/vnd.tiddlywiki","text":"<$reveal state="$:/state/notebook-sidebar" type="match" text="yes" default="no" retain="yes" animate="no">
  <$button set="$:/state/notebook-sidebar" setTo="no" tooltip="Toggle menu" class="tc-btn-invisible sidebar-toggle open">
    {{$:/plugins/mabuqian/xnote/images/bars}}
  </$button>
</$reveal>

<$reveal type="nomatch" state="$:/state/notebook-sidebar" text="yes">
  <$button set="$:/state/notebook-sidebar" setTo="yes" tooltip="Toggle menu" class="tc-btn-invisible sidebar-toggle">
    {{$:/plugins/mabuqian/xnote/images/bars}}
  </$button>
</$reveal>
"},"$:/plugins/mabuqian/xnote/ui/Search":{"title":"$:/plugins/mabuqian/xnote/ui/Search","created":"20200429191943257","modified":"20210126170723413","type":"text/vnd.tiddlywiki","text":"\define advanced-search-actions()
<$action-setfield $tiddler="$:/temp/advancedsearch/input" text={{$:/temp/notebook-search}}/>
<$action-setfield $tiddler="$:/temp/advancedsearch/refresh" text="yes"/>
<$action-navigate $to="$:/AdvancedSearch"/>
\end

\define input-accept-actions()
<$list filter="[<__tiddler__>get[text]!is[missing]] ~[<__tiddler__>get[text]is[shadow]]">
  <$action-navigate $to={{{ [<__tiddler__>get[text]] }}}/>
  <$action-deletetiddler $filter="[[$:/temp/search]] [<searchTiddler>] [<searchListState>]"/>
</$list>
\end

\define cancel-search-actions()
<$list filter="[<searchTiddler>get[text]!match{$:/temp/search}]" emptyMessage="""<$action-deletetiddler $filter="[[$:/temp/search]] [<searchTiddler>] [<searchListState>]"/>""">
  <$action-setfield $tiddler="$:/temp/search" text={{{ [<searchTiddler>get[text]] }}}/>
  <$action-setfield $tiddler="$:/temp/search/refresh" text="yes"/></$list>
\end

<$vars editTiddler="$:/temp/search"
       searchTiddler="$:/temp/search/input"
       searchListState=<<qualify "$:/state/search-list/selected-item">>>
  <$macrocall $name="keyboard-driven-input"
	      tiddler=<<editTiddler>>
	      storeTitle=<<searchTiddler>>
	      selectionStateTitle=<<searchListState>>
	      refreshTitle="$:/temp/search/refresh"
	      type="search"
	      tag="input"
	      focus={{$:/config/Search/AutoFocus}}
	      focusPopup="$:/state/popup/notebook-search"
	      class="tc-popup-handle"
	      filterMinLength={{$:/config/Search/MinLength}}
	      placeholder="Search..."
	      inputAcceptActions=<<input-accept-actions>>
	      inputCancelActions=<<cancel-search-actions>>
	      cancelPopups="yes"
	      configTiddlerFilter="[[$:/state/search/currentTab]!is[missing]get[text]] ~[{$:/config/SearchResults/Default}]"
	      />
  <$button
    tooltip={{$:/language/Buttons/AdvancedSearch/Hint}}
    aria-label={{$:/language/Buttons/AdvancedSearch/Caption}}
    class="tc-btn-invisible tc-page-controls"
    >
    {{$:/core/images/advanced-search-button}}
    <<advanced-search-actions>>
  </$button>
  <$reveal tag="div" class="tc-block-dropdown-wrapper" state="$:/state/popup/notebook-search" type="nomatch" text="" default="">
    <$list filter="[<searchTiddler>get[text]minlength{$:/config/Search/MinLength}limit[1]]" emptyMessage="" variable="listItem">
      <div class="tc-block-dropdown tc-search-drop-down">
        <$tiddler tiddler=<<configTiddler>>>
          {{$:/plugins/mabuqian/xnote/ui/Sidebar/SearchResults}}
        </$tiddler>
      </div>
    </$list>
  </$reveal>
</$vars>
"},"$:/plugins/mabuqian/xnote/ui/Sidebar/Headings":{"title":"$:/plugins/mabuqian/xnote/ui/Sidebar/Headings","created":"20200429160014174","modified":"20201210210231267","tags":"","type":"text/vnd.tiddlywiki","text":"<!--先取消掉，空出空间来 $:/plugins/mabuqian/xnote/tags/Sidebar -->
<div class="segment">
  <div class="content">
    <div class="tc-site-title">
      {{$:/SiteTitle}}
    <br/>
      {{$:/SiteSubtitle}}
    </div>
  </div>
</div>
"},"$:/plugins/mabuqian/xnote/ui/Sidebar/Search":{"title":"$:/plugins/mabuqian/xnote/ui/Sidebar/Search","created":"20200429191943257","modified":"20210124220152702","tags":"","type":"text/vnd.tiddlywiki","text":"<!-- $:/plugins/mabuqian/xnote/tags/Sidebar -->
<div class="mobile-only">
  <div class="segment">
    <div class="content search">
      {{$:/plugins/mabuqian/xnote/ui/Search}}
    </div>
  </div>
</div>
"},"$:/plugins/mabuqian/xnote/ui/Sidebar/SearchResults":{"title":"$:/plugins/mabuqian/xnote/ui/Sidebar/SearchResults","created":"20200429191943257","modified":"20210126164631418","tags":"","type":"text/vnd.tiddlywiki","text":"\define searchResultList()
  <small>{{$:/language/Search/Matches/Title}}</small>

  <$list filter="[!is[system]search:title{$(searchTiddler)$}sort[title]limit[250]]">
    <span class={{{[<currentTiddler>addsuffix[-primaryList]] -[<searchListState>get[text]] +[then[]else[tc-list-item-selected]] }}}>
      <$transclude tiddler="$:/core/ui/ListItemTemplate"/>
    </span>
  </$list>

  <small>{{$:/language/Search/Matches/All}}</small>

  <$list filter="[!is[system]search{$(searchTiddler)$}sort[title]limit[250]]">
    <span class={{{[<currentTiddler>addsuffix[-secondaryList]] -[<searchListState>get[text]] +[then[]else[tc-list-item-selected]] }}}>
      <$transclude tiddler="$:/core/ui/ListItemTemplate"/>
    </span>
  </$list>
\end

<div class="tc-search-results">
  <<searchResultList>>
</div>
"},"$:/plugins/mabuqian/xnote/ui/Sidebar/SectionTemplate":{"title":"$:/plugins/mabuqian/xnote/ui/Sidebar/SectionTemplate","created":"20200429161226897","modified":"20210202213859460","type":"text/vnd.tiddlywiki","text":"\define sidebarHeading()
<$vars tv-wikilinks="no">
  <$transclude field="caption">
    <$view field="title"/>
  </$transclude>
</$vars>
\end

<$reveal state="$:/state/notebook-sidebar-section" type="match" text=<<currentTiddler>> default="no" animate="no">
  <div class="segment section open">
    <$button set="$:/state/notebook-sidebar-section" setTo="" class="tc-btn-invisible label">
      <<sidebarHeading>>
      <span class="caret">{{$:/plugins/mabuqian/xnote/images/caret-down}}</span>
    </$button>
    <div class="content">
      <$transclude $tiddler=<<currentTiddler>> mode="block"/>
    </div>
  </div>
</$reveal>
<$reveal state="$:/state/notebook-sidebar-section" type="nomatch" text=<<currentTiddler>> default="yes" animate="no">
  <div class="segment section">
    <$button set="$:/state/notebook-sidebar-section" setTo=<<currentTiddler>> class="tc-btn-invisible label">
      <<sidebarHeading>>
      <span class="caret">{{$:/plugins/mabuqian/xnote/images/caret-right}}</span>
    </$button>
  </div>
</$reveal>
"},"$:/plugins/mabuqian/xnote/ui/Sidebar/Sections":{"title":"$:/plugins/mabuqian/xnote/ui/Sidebar/Sections","created":"20200429163239707","modified":"20210112213620486","tags":"$:/plugins/mabuqian/xnote/tags/Sidebar","type":"text/vnd.tiddlywiki","text":"
<%if [{$:/config/Sidebar/Sections}match[notebook]] %>
  <$list filter="[all[shadows+tiddlers]!has[draft.of]tag[$:/tags/SideBar]]">
    {{||$:/plugins/mabuqian/xnote/ui/Sidebar/SectionTemplate}}
</$list>

<%elseif [{$:/config/Sidebar/Sections}match[sidebar-tabs]] %>
  {{$:/plugins/mabuqian/xnote/ui/Sidebar/sidebar-tabs}}
<%elseif [{$:/config/Sidebar/Sections}match[sidebar-button]] %>
  {{$:/plugins/mabuqian/xnote/ui/Sidebar/sidebar-button}}
<%endif%>

"},"$:/plugins/mabuqian/xnote/ui/Sidebar/sidebar-button":{"title":"$:/plugins/mabuqian/xnote/ui/Sidebar/sidebar-button","type":"text/vnd.tiddlywiki","text":"<$let a={{{ [[$:/state/tab/sidebar]get[text]] }}} >
<$list filter="[all[shadows+tiddlers]tag[$:/tags/SideBar]enlist-input[]]" variable="thisTitle">
<$button>
<$action-setfield $tiddler="$:/state/tab/sidebar" text=<<thisTitle>>/>
<%if [<thisTitle>has[caption]] %>
<$wikify name="test-tid" text={{{ [<thisTitle>get[caption]] }}}>
<<test-tid>>
</$wikify>

<%else%>
  <<thisTitle>>
<%endif%>

</$button>
</$list>

<$transclude tiddler=<<a>> mode="block"/>

</$let>"},"$:/plugins/mabuqian/xnote/ui/Sidebar/sidebar-tabs":{"title":"$:/plugins/mabuqian/xnote/ui/Sidebar/sidebar-tabs","type":"text/vnd.tiddlywiki","text":"{{$:/core/ui/SideBarSegments/tabs}}"},"$:/plugins/mabuqian/xnote/ui/Sidebar/simple-search":{"title":"$:/plugins/mabuqian/xnote/ui/Sidebar/simple-search","tags":"$:/plugins/mabuqian/xnote/tags/Sidebar","type":"text/vnd.tiddlywiki","text":"<div style="display: flex; justify-content: center; align-items: center;  margin: 0; padding-left: 20px;">
      {{$:/core/ui/SideBarSegments/search}}
</div>
"},"$:/plugins/mabuqian/xnote/ui/Sidebar":{"title":"$:/plugins/mabuqian/xnote/ui/Sidebar","created":"20200428201218885","modified":"20210112213605486","tags":"$:/tags/PageTemplate","type":"text/vnd.tiddlywiki","text":"\whitespace trim
\define config-title()
$:/config/SideBarSegments/Visibility/$(listItem)$
\end

<$reveal state="$:/state/notebook-sidebar" type="match" text="yes" default="no" retain="yes" animate="no">
    <$scrollable fallthrough="no">
    <div class="nc-sidebar">
      <$list filter="[all[shadows+tiddlers]tag[$:/plugins/mabuqian/xnote/tags/Sidebar]!has[draft.of]]" variable="listItem">
        <$reveal type="nomatch" state=<<config-title>> text="hide" tag="div">
          <$transclude tiddler=<<listItem>> mode="inline"/>
        </$reveal>
      </$list>
    </div>
    </$scrollable>
</$reveal>

"},"$:/plugins/mabuqian/xnote/ui/Topbar":{"title":"$:/plugins/mabuqian/xnote/ui/Topbar","tags":"$:/tags/PageTemplate","type":"text/vnd.tiddlywiki","text":"<%if [{$:/config/xnote/topbar}match[true]] %>
<$reveal state="$:/state/notebook-topbar" type="match" text="yes" default="yes" retain="yes" animate="yes">
  <div class="nc-topbar-wrapper">
    <div class="nc-bar nc-topbar tc-adjust-top-of-scroll">
      <$list filter="[all[shadows+tiddlers]tag[$:/tags/NotebookTopbar]!has[draft.of]]" variable="listItem">
        <$reveal type="nomatch" state=<<config-title>> text="hide" tag="div">
          <$transclude tiddler=<<listItem>> mode="block"/>
        </$reveal>
      </$list>
      <div class="left">
	<!--  {{$:/plugins/mabuqian/xnote/ui/Buttons/menu}}
        {{$:/plugins/mabuqian/xnote/ui/Search}} -->
      </div>
      <div class="right">
      {{$:/core/ui/SideBarSegments/page-controls}}
      </div>
    </div>
  </div>
</$reveal>
<%endif%>
"},"$:/plugins/mabuqian/xnote/ui/Rightbar":{"title":"$:/plugins/mabuqian/xnote/ui/Rightbar","tags":"$:/tags/PageTemplate","type":"text/vnd.tiddlywiki","text":"<%if [{$:/config/xnote/rightbar}match[true]] %>
<$reveal state="$:/state/notebook-rightbar" type="match" text="yes" default="yes" retain="yes" animate="yes">
<style>
@media (min-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}} ) {
.xnote-rightmenu {
    display: block;
    padding: 0px 0px 0px 15px;
    position: fixed;
    right: 0px;
    top: 20px;
    width: 50px;
    z-index: 98;
    color: #000000;
    font-size: 20px;
    opacity: 0.25;
}
.xnote-rightmenu .tc-page-controls{
gap:10px;
}
}

@media (max-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
    .xnote-rightmenu {
        display: none; /* 例如，设置为不显示 */
    }
}
</style>
<div class="xnote-rightmenu tc-page-controls">
{{$:/core/ui/SideBarSegments/page-controls}}
</div>
</$reveal>
<%endif%>"}}}
