        "$:/plugins/nico/shields/shield": {
            "title": "$:/plugins/nico/shields/shield",
            "created": "20201215172057193",
            "modified": "20201215172122777",
            "tags": "$:/tags/Macro",
            "type": "text/vnd.tiddlywiki",
            "text": "\define shield(label,status,colour)
\whitespace trim
  <div class="shield shield-$colour$">
    <span class="label">$label$</span>
    <span class="status">$status$</span>
  </div>
\end
"
        },
        "$:/plugins/nico/shields/stylesheet": {
            "title": "$:/plugins/nico/shields/stylesheet",
            "created": "20201215172003456",
            "modified": "20201215173919738",
            "tags": "$:/tags/Stylesheet",
            "type": "text/vnd.tiddlywiki",
            "text": "\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline
.shield {
  font-weight: normal;
  display: inline-block;
  color: white;
  font-size: .8em;
}

.shield:hover {
  text-decoration: none;
}

.shield .label {
  background: #555;
  border-top-left-radius: 4px;
  border-bottom-left-radius: 4px;
  padding: 3px 6px;
}

.shield .status {
  background: #40ba12;
  border-top-right-radius: 4px;
  border-bottom-right-radius: 4px;
  padding: 3px 6px;
}

.shield.shield-purple .status {
  background: #923293;
}

.shield.shield-red .status {
  background: #cc5640;
}

.shield.shield-blue .status {
  background: #0e7fbf;
}
"
