        "$:/plugins/noteself/core/login-popup": {
            "title": "$:/plugins/noteself/core/login-popup",
            "created": "20180429151313974",
            "footer": "<$button message=\"tm-close-tiddler\">Cancel</$button>",
            "modified": "20180715121226660",
            "subtitle": "<div class='ns-login-popup-header'> {{$:/plugins/noteself/core/icons/user.svg}} <span>Login</span></div>",
            "tags": "",
            "type": "text/vnd.tiddlywiki",
            "text": "<div class=\"tc-ns-login-popup\">\n  <<tabs \"$:/plugins/noteself/core/login/noteself $:/plugins/noteself/core/login/custom\" \"$:/plugins/noteself/core/login/noteself\" \"$:/state/noteself/login-tabs\">>\n</div>"
        },
        "$:/plugins/noteself/core/login/custom": {
            "title": "$:/plugins/noteself/core/login/custom",
            "caption": "Custom",
            "text": "<div class='ns-custom-login-form'>\n    \n    <$transclude tiddler=\"$:/plugins/danielo515/tiddlypouch/ControlPanel/Database/remote-db\"/>\n    <p>Before you can login, you <strong>must save the configuration</strong></p>\n    <$reveal type=\"match\" state=\"$:/state/tiddlypouch/sync/status\" text=\"offline\">\n        <div class='input-card'>\n            <label for=\"password\">Password:</label>\n            <$edit-text tag=\"input\" name=\"password\" default=\"\" type=\"password\" tiddler=\"$:/state/ns/password\" />\n            <$set name=\"ns-form-current-value\" value={{$:/state/ns/password}}>\n                <$transclude tiddler=\"$:/plugins/noteself/core/form-error\"/>\n            </$set>\n        </div>\n        <div class='btn'>\n            <$button class=\"tc-btn-big-green\">\n                Login\n                <$action-sendmessage $message=\"tm-custom-login\" $param={{$:/state/ns/password}} />\n            </$button>\n        </div>\n    </$reveal>\n    <$reveal type=\"nomatch\" state=\"$:/state/tiddlypouch/sync/status\" text=\"offline\">\n        <div class='btn'>\n            <$button class=\"tc-btn-big-green\">\n                Logout\n                <$action-sendmessage $message=\"tm-NS-logout\"/>\n            </$button>\n        </div>\n    </$reveal>\n</div>"
        },
        "$:/plugins/noteself/core/login-form": {
            "title": "$:/plugins/noteself/core/login-form",
            "created": "20180429143826363",
            "modified": "20180501070752116",
            "tags": "",
            "type": "text/vnd.tiddlywiki",
            "text": "<div class=\"ns-login-email\">\n  <p class='login-message'>\n      In order to login we will send you an email with an unique pin.\n      <div>''No registration'' required</div>\n  </p>\n  <div class='ns-form-input-group'>\n    <div class='ns-input-row'>\n        ''email'' <$edit-text tag=\"input\" type=\"email\" tiddler=\"$:/state/ns/email\" placeholder=\"you@gmail.com\"/>\n        <$set name=\"ns-form-current-value\" value={{$:/state/ns/email}}>\n            <$transclude tiddler=\"$:/plugins/noteself/core/form-error\"/>\n        </$set>\n    </div>\n    <$button class=\"tc-btn-big-green\"> Get pin \n            <$action-sendmessage $message=\"tm-get-pin\" $param={{$:/state/ns/email}}/>\n    </$button>\n  </div>\n  <small class=\"ns-side-info\">If you want to use your own `CouchDB` server check the custom tab of this modal</small>\n</div>"
        },
        "$:/plugins/noteself/core/input-pin": {
            "title": "$:/plugins/noteself/core/input-pin",
            "created": "20180429150815436",
            "modified": "20180501202450304",
            "tags": "",
            "type": "text/vnd.tiddlywiki",
            "text": "<p class='login-message'>Please check your email. You should have received a ''PIN'' code.</p>\n\n<div class='ns-form-input-group'>\n  <div class='input-card'>\n    <p>''email'': {{$:/state/ns/email}}</p>\n    <p>''PIN'': <$edit-text tag=\"input\" tiddler=\"$:/state/ns/pin\" placeholder=\"pin\"/></p>\n    <$set name=\"ns-form-current-value\" value={{$:/state/ns/pin}}>\n      <$transclude tiddler=\"$:/plugins/noteself/core/form-error\"/>\n    </$set>\n  </div>\n  <div class='btn'>\n    <$button class=\"tc-btn-big-green\">\n    \tLogin\n    \t<$action-sendmessage $message=\"tm-validate-pin\" $param={{$:/state/ns/pin}} correlation_id={{$:/state/ns/correlation-id}}/>\n    </$button>\n  </div>\n</div>"
        },
