module.exports=function(e,t){e.inline.ruler.before("link","mention",((e,t)=>{if(t)return!1;const r=e.pos,n=e.posMax;if(64!==e.src.charCodeAt(r))return!1;let o=r+1;for(;o<n&&!e.md.utils.isSpace(e.src.charCodeAt(o));)o++;const c=e.src.slice(r+1,o);if(o<n&&40===e.src.charCodeAt(o)){o++;let t=1;for(;o<n;){const r=e.src.charCodeAt(o);if(40===r)t++;else if(41===r&&(t--,0===t))break;o++}}if(o>r+1){const t=e.push("mention","",0);return t.markup="@",t.content=c,e.pos=o,!0}return!1})),e.renderer.rules.mention=(e,t)=>{const r=e[t].content,n=document.createElement("a");return n.href=`#@${r}`,n.className="mention",n.textContent="@"+r,n.outerHTML}};","type":"application/javascript","module-type":"markdownit"},"$:/plugins/oeyoews/markdown-it-mention/style.css":{"title":"$:/plugins/oeyoews/markdown-it-mention/style.css","tags":"$:/tags/Stylesheet","text":"a.mention {
