\*/

/**
 * è·å–æŒ‡å®š Tiddler çš„è¯„è®º Tiddler æ ‡é¢˜
 * @param {string} parentTiddler - æ–‡ç«  Tiddler çš„æ ‡é¢˜
 * @returns {string} è¯„è®º Tiddler çš„æ ‡é¢˜
 */
const getCommentTiddlerTitle = (parentTiddler) =>
  `$:/comments-${parentTiddler}`;

/**
 * è·å–è¯„è®ºæ•°æ®
 * @param {string} parentTiddler - æ–‡ç«  Tiddler çš„æ ‡é¢˜
 * @returns {Array} è¯¥ Tiddler çš„è¯„è®ºåˆ—è¡¨
 */
const getComments = (parentTiddler) => {
  const commentTiddlerTitle = getCommentTiddlerTitle(parentTiddler);
  const commentTiddler = $tw.wiki.getTiddler(commentTiddlerTitle);
  return commentTiddler
    ? JSON.parse(commentTiddler.fields.comments || '[]')
    : [];
};

/**
 * æ›´æ–°è¯„è®ºæ•°æ®
 * @param {string} parentTiddler - æ–‡ç«  Tiddler çš„æ ‡é¢˜
 * @param {Array} comments - æ›´æ–°åçš„è¯„è®ºåˆ—è¡¨
 */
const updateComments = (parentTiddler, comments) => {
  const commentTiddlerTitle = getCommentTiddlerTitle(parentTiddler);

  if (comments.length === 0) {
    // å¦‚æœè¯„è®ºä¸ºç©ºï¼Œåˆ™åˆ é™¤è¯¥ Tiddler
    $tw.wiki.deleteTiddler(commentTiddlerTitle);
  } else {
    // å¦åˆ™æ›´æ–° Tiddler
    $tw.wiki.addTiddler(
      new $tw.Tiddler({
        title: commentTiddlerTitle,
        type: 'application/json',
        comments: JSON.stringify(comments, null, 0),
      }),
    );
  }
};

/**
 * æ·»åŠ è¯„è®º
 * @param {string} parentTiddler - æ–‡ç«  Tiddler çš„æ ‡é¢˜
 * @param {string} author - è¯„è®ºè€…
 * @param {string} text - è¯„è®ºå†…å®¹
 */
const addComment = (parentTiddler, author, text) => {
  const timestamp = new Date().toISOString().replace(/[-T:.Z]/g, ''); // ç”Ÿæˆå”¯ä¸€æ—¶é—´æˆ³
  const comments = getComments(parentTiddler);

  comments.push({ author, text, created: timestamp });
  updateComments(parentTiddler, comments);
};

/**
 * åˆ é™¤è¯„è®º
 * @param {string} parentTiddler - æ–‡ç«  Tiddler çš„æ ‡é¢˜
 * @param {string} timestamp - éœ€è¦åˆ é™¤çš„è¯„è®ºçš„æ—¶é—´æˆ³
 */
const deleteComment = (parentTiddler, timestamp) => {
  const comments = getComments(parentTiddler);
  const filteredComments = comments.filter(
    (comment) => comment.created !== timestamp,
  );

  if (filteredComments.length !== comments.length) {
    updateComments(parentTiddler, filteredComments);
  }
};

/**
 * ç¼–è¾‘è¯„è®º
 * @param {string} parentTiddler - æ–‡ç«  Tiddler çš„æ ‡é¢˜
 * @param {string} timestamp - éœ€è¦ç¼–è¾‘çš„è¯„è®ºçš„æ—¶é—´æˆ³
 * @param {string} newText - æ–°çš„è¯„è®ºå†…å®¹
 */
const editComment = (parentTiddler, timestamp, newText) => {
  let comments = getComments(parentTiddler);
  let updated = false;

  comments = comments.map((comment) => {
    if (comment.created === timestamp) {
      updated = true;
      return { ...comment, text: newText };
    }
    return comment;
  });

  if (updated) {
    updateComments(parentTiddler, comments);
  }
};

const getTime = (timestamp) =>
  new Date(
    timestamp
      .slice(0, 14)
      .replace(
        /(\d{4})(\d{2})(\d{2})(\d{2})(\d{2})(\d{2})/,
        '$1-$2-$3T$4:$5:$6Z',
      ),
  )
    .toISOString()
    .replace(RegExp(/[TZ]/g), ' ')
    .trim();

module.exports = {
  addComment,
  getComments,
  deleteComment,
  editComment,
  getTime,
};
