const{widget:e}=require("$:/core/modules/widgets/widget.js");exports.neotwCopyCode2=class extends e{constructor(e,t){super(e,t)}render(e,t){if(!$tw.browser)return;if(this.computeAttributes(),this.execute(),this.document.isTiddlyWikiFakeDom)return;const o=`[data-tiddler-title="${this.getVariable("currentTiddler")}"]`,s=document.querySelector(o),r=s.querySelectorAll("pre");0!==r.length&&r&&(s.querySelectorAll("pre > code"),r.forEach((e=>{if(e.clientHeight<400)return;e.classList.add("code-collapsed");const t=document.createElement("button");t.className="expand-button absolute bottom-2 left-1/2 transform -translate-x-1/2",t.textContent="显示全部",e.appendChild(t),t.addEventListener("click",(function(){e.classList.contains("code-collapsed")?t.textContent="收起代码":t.textContent="显示全部",e.classList.toggle("code-collapsed")}))})))}};","type":"application/javascript","module-type":"widget"},"$:/plugins/oeyoews/neotw-copy-code2/viewTemplate":{"title":"$:/plugins/oeyoews/neotw-copy-code2/viewTemplate","text":"<!-- tags: $:/tags/ViewTemplate -->
<!-- 会造成保存样式异常， 性能下降 -->

<$list filter="[all[current]!is[missing]!is[binary]]">
<$neotwCopyCode2 />
</$list>"}}}
