title: $:/plugins/oeyoews/neotw-vue-journal/component.js
type: application/javascript
module-type: library

\*/

const getTemplate = require('$:/plugins/oeyoews/neotw-vue3/getTemplate.js');

const journals = $tw.wiki.filterTiddlers(
  `[tag[Journal]!sort[created]limit[99]]`
);

const app = () => {
  const component = {
    setup() {
      return {
        journals
      };
    },

    methods: {
      gotoTiddler(e) {
        const title = e.target.dataset.title;
        if (!title) return;
        new $tw.Story().navigateTiddler(title);
      },
      renderTiddler(tiddler) {
        return $tw.wiki.renderTiddler('text/html', tiddler);
      }
    },

    template: getTemplate('$:/plugins/oeyoews/neotw-vue-journal/widget.vue')
  };
  return component;
};

module.exports = app;
","type":"application/javascript","module-type":"library"},"$:/plugins/oeyoews/neotw-vue-journal/widget.js":{"title":"$:/plugins/oeyoews/neotw-vue-journal/widget.js","text":"/*\
title: $:/plugins/oeyoews/neotw-vue-journal/widget.js
type: application/javascript
module-type: widget

neotw-vue-journal widget

\*/
const { widget: Widget } = require('$:/core/modules/widgets/widget.js');

class ExampleWidget extends Widget {
  constructor(parseTreeNode, options) {
    super(parseTreeNode, options);
  }

  render(parent, nextSibling) {
    if (!$tw.browser) return;

    this.computeAttributes();
    this.execute();

    const ssr = this.document.isTiddlyWikiFakeDom;
    if (ssr) return;

    const vuelib = '$:/plugins/oeyoews/neotw-vue3/vue.global.prod.js';

    if (!window.Vue) {
      window.Vue = require(vuelib);
    }

    const { createApp } = window.Vue;
    const component = require('./component');
    const domNode = this.document.createElement('div');

    try {
      const app = createApp(component());

      app.config.errorHandler = (err) => {
        const text = `[Vue3](${app.version}): ` + err;
        console.error(text);
        domNode.textContent = text;
        domNode.style.color = 'red';
      };

      // 挂载
      app.mount(domNode);

      parent.insertBefore(domNode, nextSibling);
      this.domNodes.push(domNode);
    } catch (e) {
      console.error(e.message);
    }
  }
}

/** @description neotw-vue-journal widget */
exports['neotw-vue-ht'] = ExampleWidget;
","type":"application/javascript","module-type":"widget"},"$:/plugins/oeyoews/neotw-vue-journal/widget.vue":{"title":"$:/plugins/oeyoews/neotw-vue-journal/widget.vue","text":"<template>
