<!-- tc-modal-centered  -->"},"$:/esc":{"title":"$:/esc","text":"<div class="flex justify-between">
{{!!caption}}
<$button class="bg-transparent">
  <span class="hover:bg-rose-200 rounded px-2 py-1 text-xs">Esc</span>
<$action-sendmessage $message="tm-close-tiddler" $param=<<currentTiddler>>/>
</$button>
</div>
"},"$:/plugins/oeyoews/tiddlywiki-modal-ui/style-dark":{"title":"$:/plugins/oeyoews/tiddlywiki-modal-ui/style-dark","tags":"$:/tags/Stylesheet","text":"<% if [[$:/palette]get[text]get[color-scheme]match[dark]] %>
  .o-tm .tc-modal,
  .o-tm .tc-modal-header,
  .o-tm .tc-modal-body,
  .o-tm .tc-modal-footer {
    /* Dark 模式下的样式属性 */
    background-color: black; /* 例如，将背景颜色更改为暗色调 */
    color: #fff; /* 例如，将文本颜色更改为白色 */
  }
<% endif %>"},"$:/plugins/oeyoews/tiddlywiki-modal-ui/style":{"title":"$:/plugins/oeyoews/tiddlywiki-modal-ui/style","text":".o-tm .tc-modal,
.o-tm .tc-modal-header,
.o-tm .tc-modal-body,
.o-tm .tc-modal-footer {
  border-radius: 10px;
  border: none;
  background-color: #f5f5f5;
  /* min-width: 60%; */
  min-height: 40%;
}

.o-tm .tc-modal-footer {
  display: none;
}

.o-tm .tc-modal-header {
  text-transform: capitalize;
}

.o-tm .tc-modal-backdrop {
  cursor: pointer;
}
","tags":"$:/tags/Stylesheet"}}}
