title: $:/plugins/oeyoews/vue-echarts-addon/app.js
type: application/javascript
module-type: library

\*/

const { ref } = window.Vue;

const getTemplate = require('$:/plugins/oeyoews/neotw-vue3/getTemplate.js');
const list = $tw.wiki.filterTiddlers(
  '[tag[Journal]short[yes]!sort[created]limit[9]]'
);

const content = list.map((title) => {
  return $tw.wiki.renderTiddler('text/html', title, {
    parseAsInline: true
  });
});

const app = () => {
  const component = {
    template: getTemplate(
      '$:/plugins/oeyoews/vue-echarts-addon/templates/app.vue'
    ),
    data() {
      return {
        newsItems: content,
        currentIndex: 0,
        scrollSpeed: 3000 // 滚动速度，单位为毫秒
      };
    },
    mounted() {
      this.startScroll();
    },
    methods: {
      startScroll() {
        setInterval(() => {
          this.scrollNews();
        }, this.scrollSpeed);
      },
      scrollNews() {
        const newsList = this.$refs.newsList;
        const newsItems = newsList.querySelectorAll('li');
        const currentNewsItem = newsItems[this.currentIndex];

        currentNewsItem.scrollIntoView({
          behavior: 'smooth',
          block: 'center'
        });

        this.currentIndex = (this.currentIndex + 1) % newsItems.length;
      }
    }
  };
  return component;
};

module.exports = app;
","type":"application/javascript","module-type":"library"},"$:/plugins/oeyoews/vue-echarts-addon/widget.js":{"title":"$:/plugins/oeyoews/vue-echarts-addon/widget.js","text":"/*\
title: $:/plugins/oeyoews/vue-echarts-addon/widget.js
type: application/javascript
module-type: widget

vue-echarts-addon widget

\*/
const { widget: Widget } = require('$:/core/modules/widgets/widget.js');

class ExampleWidget extends Widget {
  constructor(parseTreeNode, options) {
    super(parseTreeNode, options);
  }

  render(parent, nextSibling) {
    if (!$tw.browser) return;

    this.computeAttributes();
    this.execute();

    const ssr = this.document.isTiddlyWikiFakeDom;
    if (ssr) return;

    const vuelib = '$:/plugins/oeyoews/neotw-vue3/vue.global.prod.js';

    if (!window.Vue) {
      window.Vue = require(vuelib);
    }

    const { createApp } = window.Vue;
    const component = require('./app');
    const domNode = this.document.createElement('div');
    const TiddlyWikiVue = require('./plugins/TiddlyWikiVue');

    try {
      const app = createApp(component());

      app.use(TiddlyWikiVue);

      app.config.errorHandler = (err) => {
        const text = `[Vue3](${app.version}): ` + err;
        console.error(text);
        domNode.textContent = text;
        domNode.style.color = 'red';
      };

      // 挂载
      app.mount(domNode);

      parent.insertBefore(domNode, nextSibling);
      this.domNodes.push(domNode);
    } catch (e) {
      console.error(e.message);
    }
  }

  // TIP: 界面由 vue 接管， 不要在这里刷新
  refresh() {
    return false;
  }
}

/** @description vue-echarts-addon widget */
exports['widget-wIiAOQFrea'] = ExampleWidget;
