  .tc-tiddler-frame > * {
    transition: opacity 0.25s ease;
  }

  .krystal-link--active {
    background: <<color message-background>>;
  }

  .krystal-tiddler__frame--hide > *:not(.krystal-tiddler__title) {
    opacity: 0;
  }

  .krystal-tiddler__frame--overlay {
    box-shadow: 0 0 1em rgba(0, 0, 0, 0.125);
  }

  .krystal-tiddler__frame--maximized {
    left: 0 !important;
    right: 0 !important;
    bottom: 0;
    width: 100%;
    z-index: 5;
  }

