{
    "tiddlers": {
        "$:/plugins/sq/links-context-menu/contextmenu": {
            "title": "$:/plugins/sq/links-context-menu/contextmenu",
            "tags": "$:/tags/PageTemplate",
            "text": "\define menu-item(title,subtitle:"",actions:"")
	<$button class="tc-menu-list-item tc-btn-invisible" actions=<<__actions__>> >
		<span class="sq-contextmenu-itemwrapper">
			<span class="sq-contextmenu-itemtext"><$text text=<<__title__>>/></span>
			<span class="sq-contextmenu-itemicon"><$text text=<<__subtitle__>>/></span>
		</span>
	</$button>
\end

\define menu-divider() <div class="sq-contextmenu-divider"/>
<$vars currentTiddler={{{[[$:/state/sq/links-context-menu/]get[current]]}}}>
	<$reveal type="popup" state="$:/state/sq/links-context-menu/">
		<div class="tc-drop-down sq-contextmenu sq-etl-contexmenu">
			<$list filter="[all[shadows+tiddlers]tag[$:/tags/links-context-menu/menuitems]sort[sq-contextmenu-name]]" variable="listItem">
				<$macrocall
					$name="menu-item"
					title={{{[<listItem>get[sq-contextmenu-name]]}}}
					subtitle={{{[<listItem>get[sq-contextmenu-subtitle]]}}}
					actions={{{[<listItem>get[text]]}}}
				/>
			</$list>
		</div>
	</$reveal>
</$vars>"
        },
        "$:/plugins/sq/links-context-menu/link.js": {
            "title": "$:/plugins/sq/links-context-menu/link.js",
            "text": "/*\
title: $:/plugins/sq/links-context-menu/link.js
type: application/javascript
module-type: widget-subclass
Link widget override for renderLink method
\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports.baseClass = "link"; //

//exports.name = "link";

exports.constructor = function(parseTreeNode,options) {
	this.initialise(parseTreeNode,options);
};

exports.prototype = {};

exports.prototype.renderLink = function(parent,nextSibling) {
	Object.getPrototypeOf(Object.getPrototypeOf(this)).renderLink.call(this,parent,nextSibling);

	var link = this.domNodes[0];
	var self = this;
	if($tw.utils.hasClass(link,"tc-tiddlylink-resolves") || $tw.utils.hasClass(link,"tc-tiddlylink")) {
		link.addEventListener("contextmenu",function(event) {
			if(event.ctrlKey) { //todo make this key configurable
				return false;
			}
			event.preventDefault();

			var rect = link.getBoundingClientRect();

			$tw.popup.triggerPopup({
				//domNode: link,
				domNodeRect: {
					"left": rect.left + window.scrollX,
					"top": rect.top + window.scrollY,
					width: link.offsetWidth,
					height: link.offsetHeight
				},
				title: "$:/state/sq/links-context-menu/",
				wiki: self.wiki,
				noStateReference: true
			});
			self.wiki.setText("$:/state/sq/links-context-menu/","current",undefined,self.to);

		});
	}

}


})();


