            "type": "application/javascript",
            "module-type": "widget-subclass"
        },
        "$:/plugins/sq/links-context-menu/menuitems/edit-tiddler": {
            "title": "$:/plugins/sq/links-context-menu/menuitems/edit-tiddler",
            "sq-contextmenu-name": "Open for editing",
