/**** sq-ui-btn-big ****/

.squi-btn-big {
	box-shadow: inset 0px 1px 0px 0px #ffffff;
	background: linear-gradient(to bottom, #ffffff 5%, #dedbdb 100%);
	/* f6f6f6*/
	background-color: #ffffff;
	border-radius: 6px;
	border: 1px solid #dcdcdc;
	display: inline-block;
	cursor: pointer;
	color: #666666;
	font-family: Arial;
	font-size: 18px;
	font-weight: bold;
	padding: 19px 32px;
	text-decoration: none;
	text-shadow: 0px 1px 0px #ffffff;
}

.squi-btn-big:enabled:hover {
	background: linear-gradient(to bottom, #dedbdb 5%, #ffffff 100%);
	background-color: #f6f6f6;
}

.squi-btn-big:disabled {
	background: linear-gradient(to bottom, #ccc 5%, #ccc 100%);
	background-color: #ccc;
	opacity: 0.6;
	cursor: default;
}

.squi-btn-big:active {
	position: relative;
	top: 1px;
}


