text:
{
    "tiddlers": {
        "$:/tesseract/Moments/Featured": {
            "text": "<$list filter="[tag[Featured]!sort[created]limit[1]]">
<div class="main wrapper clearfix" style="top: 0;left: 0;position: absolute;width:100%">
		        <section class="featured">        			
	               	<!-- begin sticky post article -->
	               	<article class="post-68 post type-post status-publish format-gallery sticky hentry category-thoughts post_format-post-format-gallery">

	                    <header>

                    		<!-- begin postmeta -->
	                    	<div class="postmeta">
	                        	<span><i class="fa fa-clock-o"></i><$view field="modified" format="date" template={{$:/language/Tiddler/DateFormat}}/></span>
	                        	<$reveal type="nomatch" state="$:/status/UserName" text=""><span><i class="fa fa-user"></i> Posted by {{$:/status/UserName}}</span></$reveal>
	                        	<span><i></i> <a href="#"><$list filter="[is[current]tags[]!prefix[$:/]] -Featured">&nbsp;&nbsp;&nbsp;<<currentTiddler>>    </$list></a></span>

	                        </div>
	                        <!-- end postmeta -->

										<!-- begin post icon -->
										<div class="icon">

											<i class="fa fa-picture-o fa-2x">{{$:/core/images/quote}}</i>

										</div>
										<!-- end post icon -->

	                      <$link><h1><a>{{!!title}}</a></h1></$link>

	                        <a class="featured_post">Featured Post</a>

	                    </header>

	                </article>
	                <!-- end sticky post article -->


	   			</section>
	   			<!-- featured article -->
</div>

</$list>",
            "title": "$:/tesseract/Moments/Featured",
            "tags": "$:/tesseract/Moments $:/tags/AboveStory",
            "modifier": "Riz
",
            "modified": "20161124102508556",
            "creator": "Riz
",
            "created": "20161122135842345"
        },
        "$:/core/ui/TopBar/menu": {
            "created": "20161002082547234",
            "creator": "Dr. Rizwan Ishak",
            "text": "<div class="mobile">
<$reveal state="$:/state/sidebar" type="nomatch" text="no" default="no">
<$button set="$:/state/sidebar" setTo="no" tooltip={{$:/language/Buttons/HideSideBar/Hint}} aria-label={{$:/language/Buttons/HideSideBar/Caption}} class="roundbtn">
<span>{{$:/core/images/menu-button}}</span>
{{$:/irreverent-mudblood/overlay-sidebar/backgroundstyle}}
</$button>
</$reveal>
<$reveal state="$:/state/sidebar" type="match" text="no" default="no">
<$button set="$:/state/sidebar" setTo="yes" tooltip={{$:/language/Buttons/ShowSideBar/Hint}} aria-label={{$:/language/Buttons/ShowSideBar/Caption}} class="roundbtn"><span>{{$:/core/images/menu-button}}</span>
{{$:/irreverent-mudblood/overlay-sidebar/backgroundstyle2}}
</$button>
</$reveal>
</div>",
            "type": "text/vnd.tiddlywiki",
            "title": "$:/core/ui/TopBar/menu",
            "tags": "$:/OverlaySidebar $:/tesseract/Moments $:/tags/TopRightBar",
            "revision": "5",
