            "modified": "20161123180503386",
            "bag": "default"
        },
        "$:/irreverent-mudblood/overlay-sidebar/backgroundstyle": {
            "created": "20161030153934135",
            "text": "<style>
@media only screen and (min-width: 768px) {
.tc-sidebar-scrollable { 
background-color: #FEFEFE; 
z-index: 1000;
padding: 42px 0 28px 42px;
width:45%;
border-left: 1px solid #e1e1e1;
}
}
@media only screen and (max-width: 768px) {
.tc-sidebar-scrollable { 
background-color:#FEFEFE; 
z-index: 1000; 
width:100%; 
left:10%;
}
}
</style>",
            "bag": "default",
            "revision": "1",
            "type": "text/vnd.tiddlywiki",
            "title": "$:/irreverent-mudblood/overlay-sidebar/backgroundstyle",
            "tags": "$:/OverlaySidebar",
            "modified": "20161030171447906"
        },
        "$:/irreverent-mudblood/overlay-sidebar/stylesheet": {
            "created": "20161030153009897",
            "text": ".roundbtn {
background-color:#FFFDE7;
font-size: 25px; 
position: relative;
display: inline-block;
width: 35px;
height: 35px;
cursor: pointer; 
border: none; 
border-radius:100px; 
box-shadow: 0 2px 5px 0 rgba(0, 0, 0, .26);fill: #fff;
}

.roundbtn span {
font-size:15px;  
position: absolute;  
top: 0;  right: 0;    
bottom: 0;    
left: 0;    
overflow: auto;    
width: 50%;    
height: 50%;    
margin: auto; fill: #fff;
}


.tc-sidebar-lists .tc-search .tc-popup-handle {
border: 1px solid #ececec;
}
.tc-sidebar-lists .tc-search .tc-popup-handle:focus {
border: 1px solid #c4c4c4;
}",
            "bag": "default",
            "revision": "4",
            "type": "text/vnd.tiddlywiki",
            "title": "$:/irreverent-mudblood/overlay-sidebar/stylesheet",
            "tags": "$:/tags/Stylesheet $:/OverlaySidebar",
            "modified": "20161030164310029"
        },
        "$:/irreverent-mudblood/overlay-sidebar/backgroundstyle2": {
            "created": "20160829073842702",
            "creator": "Dr. Rizwan Ishak",
            "text": "<style>.tc-sidebar-scrollable { background-color: rgba(0,0,0,0.0); z-index: -1; }</style>",
            "title": "$:/irreverent-mudblood/overlay-sidebar/backgroundstyle2",
            "modifier": "Dr. Rizwan Ishak",
            "modified": "20161030154343370",
            "tags": "$:/OverlaySidebar"
        },
        "$:/tesseract/Moments/Main": {
            "created": "20161122135953210",
            "creator": "Riz
",
            "text": "/* ==========================================================================
   HTML5 Boilerplate styles
   ========================================================================== */

/* System */
.tc-story-river {padding: 350px 42px 42px 42px !important;background: #FAF9F5;}
body.tc-body {background: #FAF9F5;}
@media (max-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
.tc-story-river {padding: 300px 2px 35px 2px !important;background: #FAF9F5;}
}

html,
button,
input,
select,
textarea {
    color: #222;
}

body {
    font-size: 1em;
    line-height: 1.4;
}

::-moz-selection {
    background: #b3d4fc;
    text-shadow: none;
}

::selection {
    background: #b3d4fc;
    text-shadow: none;
}

hr {
    display: block;
    height: 1px;
    border: 0;
    border-top: 1px solid #ccc;
    margin: 1em 0;
    padding: 0;
}

img {
    vertical-align: middle;
}

fieldset {
    border: 0;
    margin: 0;
    padding: 0;
}

textarea {
    resize: vertical;
}

.chromeframe {
    margin: 0.2em 0;
    background: #ccc;
    color: #000;
    padding: 0.2em 0;
}

:focus {
	outline: 0;
}
ins {
	text-decoration: none;
}
del {
	text-decoration: line-through;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}
.alignleft,
img.alignleft {
	display: inline;
	float: left;
  	margin-right: 1.5em;
  	padding: 4px;
}
.alignright,
img.alignright {
	display: inline;
	float: right;
  	margin-left: 1.5em;
}
.aligncenter,
img.aligncenter {
	clear: both;
	display: block;
	margin-left: auto;
	margin-right: auto;
}
.wp-caption {
	text-align: center;
	margin-bottom: 1.5em;
}
.wp-caption img {
	border: 0 none;
	margin: 0;
	padding: 0;
}
.wp-caption p.wp-caption-text {
	margin: 0;
}
.wp-smiley {
	max-height: 1em;
	margin:0 !important;
}
.gallery dl {
	margin: 0;
}
.gallery-caption {
	margin:-1.5em 0 0 0;
}
blockquote.left {
	float: left;
	margin-left: 0;
	margin-right: 20px;
	text-align: right;
	width: 33%;
}
blockquote.right {
	float: right;
	margin-left: 20px;
	margin-right: 0;
	text-align: left;
	width: 33%;
}


/* ===== Main Styles ==================================================

   ========================================================================== */

body {
    font: 16px/26px Source Sans Pro, sans serif;
    color:#666659;
    background:#FAF9F5;
}

::selection 	 	{ background: rgb(67, 202, 204 0.17); color:#ffffff }
::-moz-selection 	{ background: rgb(67, 202, 204); color:#ffffff }
img::selection 		{ background: transparent; color:#ffffff}
img::-moz-selection	{ background: transparent; color:#ffffff }
body 				{-webkit-tap-highlight-color: rgb(67, 202, 204);}





h1,h2,h3,h4,h5,h6  {
    color:#2A2A26;
}
@media (max-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
h1, h2, h3, h4, h5, h6 {
	line-height: 0.9 !important;
	font-weight: 300 !important;
}
}

blockquote {
    border-left: 2px solid #1EB2B2 !important;
    font-size: 120% !important;
    font-style: italic;
    margin: 0;
    padding-left: 1.5em;
}

.main-container {
    width: 100%;
    height:100%;
    display: table;
}

.wrapper {
    margin: 0%;
}



/* ===================
    ALL: Default Theme
   =================== */

.header_container_fixed{
	background: #2A2A26;
	float: left;
    display:table;
    width: 220px;
    height:100%;
    position:fixed;
    z-index: 1;
left:0;
top:0;
}

.header_container_flat{
	background: #2A2A26;
	float: left;
    display:table;
    width: 220px;
    height:100%;
    z-index: 1;
}

.main_content {
	position:relative;
	margin-left:215px;
}

.logo {
	color: white;
	clear: both;

	text-align: center;
	margin-top: 65px;
}

.textlogo {
    color: white;
    clear:both;
    text-align: center;
    margin-top:35px;
}

.textlogo a {
    color: white;
    text-transform:uppercase;
}

.description {
    color: white;
    clear:both;
    text-align: center;
    font-size:16px;
    font-family:Georgia;
    font-style:italic;
    font-weight:normal;
    width:65%;
    margin: 0 auto;
}

.mini_divider {
    border: 1px dashed #43443F;
    width:90%;
    text-align:center;
    margin:30px auto 30px auto;
}

.featured_post {
    color: white;
    clear:both;
    background: #1F6D6F;
    font-family:'Source Sans Pro', sans serif;
    font-size: 11px;
    text-transform:uppercase;
    padding:5px;
}

.featured_post:hover {
    color: #43CACC;
}
.featured a.tc-tiddlylink:hover {
    text-decoration: none;
}
.more-link {
	color: #43CACC;
    text-transform:uppercase;
    font-weight:bold;
    text-transform:uppercase;
    clear:both;
    display:block;
    margin:20px 0px 0px;
}

.more-link:hover {
	color: #2A2A26;
}

#social {
    margin-bottom: 30px;
}

/* ==============
    MOBILE: Menu
   ============== */

nav ul {
    margin: 0;
    padding: 0;
}

nav ul li a {
    display: block;
    margin-bottom: 10px;
    text-align: center;
    text-decoration: none;
    font-weight: bold;
    font-family:'Source Sans Pro', sans serif;
    color: #8C8C7B;
    width: 100%;
    padding: 10px 0;
    margin-bottom: 0;
    text-transform:uppercase;
}

nav ul li a:hover,
nav ul li a:visited {
    color: #FFFFFF;
    text-decoration: none;
}

nav ul li.current-menu-item a {
    text-decoration: none;
    color: #FFFFFF;
}

.search_box {
    margin: 0 auto;
    text-align.center;
    width:80%;
}

.search_box input {
    background-image: url("../img/searchbg.png");
    background-position: 90% center;
    background-repeat: no-repeat;
    border: medium none;
    border-radius: 15px;
    padding: 10px 10px;
    font-family:'Georgia', sans serif;
    font-size:12px;
    font-weight:normal;
    font-style:italic;
    color: #8C8C7B;
    width:90%;


}

/* ==============
    MOBILE: Main
   ============== */

.main {
    padding: 0px;
}

.main article h1 {
    font-size: 42px;
}

.main .featured{
    background:#2FBFC0;
background:{{$:/tesseract/userSettings!!topbarBg}};
}

.main .topbar{
    background:#2FBFC0;
    margin-bottom:35px;
    height:15px;
}

.main .featured header {
    margin-left: 280px;
    width: 763px;
}

.main .featured article{
    padding:30px 0;
    width: 100%;

}

.main .featured article h1 a {
    font-size: 46px;
    color:#FFFFFF;
    line-height: 1;
    font-family:'Source Sans Pro', sans serif;
    	-webkit-transition: all 0.4s ease-out;
    	-moz-transition: all 0.4s ease-out;
    		transition: all 0.4s ease-out;
}

.main .featured article h1 a:hover {
    color:#2A2A26;
}

.main .featured article .icon {
    background-color: #FFFFFF;
    border-radius:5px;
    float: right;
    height: 47px;
    width: 50px;
    margin-top: 10px;
    position: relative;
    right: 0;
    top: 38px;
    text-align:center;
    color:#43CACC;
    box-shadow: -1px -2px 2px rgba(0, 0, 0, 0.1) inset;
}

.main article h2 {
    font-size: 36px;
}


.error404 .main article h2, .search-no-results .main article h2 {
    font-size: 36px;
    text-align:center;
    line-height:42px;
}

.entry-colors {
	left: 0;
	position: relative;
	top: 0;
	width: 100%;
	margin-top: 25px;
}

.main article h2 a {
	color:#2A2A26;
	font-family:'Source Sans Pro', sans serif;
    	-webkit-transition: all 0.4s ease-out;
    	-moz-transition: all 0.4s ease-out;
    		transition: all 0.4s ease-out;
}

.main article h2 a:hover {
	color:#43CACC;
}

.main article h2 .post_title_icon {
    background-image: url("../img/post_title_icon.png");
    background-position: left;
    background-repeat: no-repeat;
    display:inline-table;
    width:57px;
    height:28px;
    position:relative;
    margin-left:-75px;
    margin-right:20px;
}
.main .featured .postmeta span, .main .featured .postmeta span a {
    font-size: 11px;
    color:#196768;
    line-height: 1;
    text-transform:uppercase;
    letter-spacing:2px;
    padding-right:10px;
    font-family:'Source Sans Pro', sans serif;
}

.main article .postmeta span, .main article .postmeta span a {
    font-size: 11px;
    color:#A3A3A3;
    line-height: 1;
    text-transform:uppercase;
    letter-spacing:3px;
    padding-right:10px;
    font-family:'Source Sans Pro', sans serif;
}

.main article .postmeta span a {
    padding: 0 !important;
}

.footer-container footer {
    color: #666659;
    padding: 20px;
    margin:0 auto;
    text-align:center;
    font-size:12px;
    background:#FFFFFF;
}

.main .page-header {
    margin-bottom: 65px;
    margin-top: 40px;
}

.main .page-title {
    font-size: 18px;
    line-height: 0;
    margin: 0;
    text-transform: uppercase;
}

.main .taxonomy-description {
    font-family: Georgia;
    font-size: 14px;
    font-style: italic;
}


/*BLOG COMMENTS
 ******************************************************************/
#respond, #comments{
    max-width:100%;
}

#comments article{
    width:auto;
}

h3#comments, h3#commentsForm {
    font-size: 26px;
    margin:25px 0;
}

#comments > h3 {
    font-size: 26px;
    font-family:'Source Sans Pro', sans serif;
    margin-top:0px;
}

ul.commentlist {
    list-style: none;
    list-style-position: outside;
    display: block;
    margin: 30px 0 0;
    padding: 30px;
    width:700px;
    background:#FFFFFF;
}

ul.commentlist ul {
    list-style-type: none;
    list-style-position: outside;
    padding: 0;
    margin-left:35px;
}

ul.commentlist li {
    position: relative;
}

ul.commentlist li article {
    padding-left:60px;
}

ul.commentlist li p {
    margin: 5px 0;
    padding: 5px 0;
    color:#2D2D27;
    font-size:16px;
}

ul.commentlist li .comment-meta span {
    font-size: 12px;
    color: #999;
}

ul.commentlist li header {
    font-size: 12px;
}

ul.commentlist li .avatar {
    background: #fff;
    padding: 1px;
    border: none;
    position: absolute;
    left: 0px;
    top: 0px;
    border-radius:30px;
}

ul.commentlist li .comment_content {
    padding:15px 0;
}

ul.commentlist li cite {
    font-style: normal;
    font-weight: bold;
    font-size: 16px;
    color: #2DBFBF;
}

ul.commentlist .comment-reply-link {
    color: #43CACC;
    display: block;
    font-family:'Source Sans Pro', sans serif;
    font-size: 12px;
    font-weight: bold;
    position: absolute;
    left: 5px;
    top: 60px;
    padding: 1px 0;
    text-transform: uppercase;
    width:40px;
    text-align: center;
}

ul.commentlist .comment-reply-link:hover {
    color:#333;
    text-decoration: none;
}

ul.commentlist time a, .comment-edit-link {
    color:#B0B0AC;
    text-decoration: none;
}

ul.commentlist time a:hover {
    color:#43CACC;
}

ul.commentlist li .children li {
    margin-top: 10px;
    overflow: hidden;
    padding-bottom: 0;
}


ul.commentlist li .children li .reply {
    top: 20px;
}

.comments-logged-in-as, #cancel-comment-reply-link {
    font-size: 14px;
    text-transform: uppercase;
}

/* CONTACT F0RM
---------------------------------------------------------*/
#contactForm {
	display:block;
    padding-top: 30px;
}
.input input[type="text"],
.input input[type="email"],
.input input[type="url"],
.input input[type="password"],
.input textarea {
    background: #ffffff;
    border:1px solid #efefef;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
    padding:10px;
    font-family:Georgia;
    font-size:12px;
    font-style:italic;
    color:#999999;
}
.input textarea {
	resize:vertical;
    max-height:300px;
    min-height:200px;
}
.input label {
    display: block;
    font-size: 14px;
    margin-bottom: 5px;
}
.input input {
    width: 88%;
}
.input textarea {
    width: 94%;
    height: 200px;
}

.input input:focus,
.input textarea:focus {
    -webkit-box-shadow:  0px 0px 5px 0 #efefef;
    moz-box-shadow:  0px 0px 5px 0 #efefef;
    box-shadow:  0px 0px 5px 0 #efefef;
}


.thanks {
	display: none;
}
label sup {
  color:#E3006A;
}
.form-row {
    clear: both;
    margin-bottom: 10px;
}

.col_half {
    width: 50%;
    float: left;
    margin-bottom: 10px;
}

.first,
.last,
.col_half:nth-child(2n+1) {
    clear: both;
}


.last {
clear: right;
margin-right: 0 !important;
}

.col_half p{
    margin-top: 0;
}

#contactForm .error, .error {
	font-size: 12px;
    color: #EC3233;
    display: none;
    margin-bottom: 5px;
    width: 90%;
}

#contact-map{
	background: #fff;
	margin-bottom:30px;
	border:1px solid #efefef;
	padding:10px;
}
#contact-map iframe{
	border:none;
}

/* FOOTER SOCIAL
---------------------------------------------------------*/
#social a {
    font-size: 20px;
    padding-right: 10px;
}

/* FOOTER SOCIAL
---------------------------------------------------------*/
.emm-paginate{
    padding: 10px 0 40px;
}

.emm-paginate a, .emm-paginate span {
    border-bottom: 1px solid #999;
    color: #333;
    text-shadow: 1px 1px #fff;
    padding: 4px 10px;
    text-align: center;
    text-decoration: none;
    -webkit-transition: color 0.1s ease-in;
    -moz-transition: color 0.1s ease-in;
    -o-transition: color 0.1s ease-in;
    -ms-transition: color 0.1s ease-in;
    transition: color 0.1s ease-in;
    text-shadow: none;
    margin: 0 4px;
    border-radius: 4px;
    border: 1px solid #ddd;
}

.emm-paginate .emm-title {
    border:none;
    padding-left:0;
}

.emm-paginate .emm-current,.emm-paginate a:hover {
    color:#1EB2B2;
}

.emm-paginate .emm-current{
    font-weight:bold;
}

.emm-paginate a:hover {
    -webkit-box-shadow: 0 0 3px rgba(35,194,248,.3);
    -moz-box-shadow:0 0 3px rgba(35,194,248,.3);
    box-shadow:0 0 3px rgba(35,194,248,.3);
    border-color:#fff;
}

/* COMMENTS F0RM
---------------------------------------------------------*/
#commentform {
    padding-bottom:30px;
}

#commentform ul {
    padding:0;
    list-style:none;
}

#comment-form-title {
    clear: both;
    color: #30302C;
    font-family: 'Source Sans Pro',sans serif;
    font-size: 22px;
}

#commentform label {
    display: block;
    padding: 5px 0;
}

#commentform input[type="text"],#commentform input[type="email"],#commentform input[type="url"],#commentform textarea {
    border: 1px solid #ddd;
    border-radius: 3px;
    padding: 10px;
    width: 100%;
    background: #FFF;
    margin-top: 5px;
    -webkit-transition: background 0.2s ease-in;
    -moz-transition: background 0.2s ease-in;
    -o-transition: background 0.2s ease-in;
    -ms-transition: background 0.2s ease-in;
    transition: background 0.2s ease-in;
    color: #666;
    margin-bottom: 20px;
}

#commentform input[type="text"]:focus,#commentform input[type="email"]:focus, #commentform input[type="url"]:focus,#commentform textarea:focus {
    background: #F2FDFD;
}

#commentform textarea {
    font-family:'Source Sans Pro', sans serif;
    line-height: 150%;
}

#commentform input#submitcomment, input[type="submit"] {
    background-color:#EE4341;
    border:none;
    font-family:'Source Sans Pro', sans serif;
    font-size:16px;
    font-weight:bold;
    display:inline-block;
    padding:10px 30px;
    text-decoration:none;
    cursor:pointer;
    line-height:1;
    color:#FFFFFF;
}

#commentform .subscribe-to-comments label {
    display: inline;
