}
table td {
    padding: 10px !important;
}
table tr {
    border-bottom: 1px dotted #AEADAD !important;
}

::selection {
  background: #FFF5B8 !important;
  color: #000 !important;
  display: block !important; }

::-moz-selection {
  background: #FFF5B8 !important;
  color: #000 !important;
