margin-left:10px;
}


/* contents Tab on Sidebar*/
nav > ul > span div.sidebarpopup > div.tc-table-of-contents > p > ol.tc-toc > li  a.tc-tiddlylink {text-align:left;display: inline;
text-transform: none;font-size:15px;}


