        "$:/themes/tg/mobie/styles": {
            "created": "20170501180648530",
            "text": "<pre>
/* Sidebar menu style */
[data-tiddler-title^="$:/core/ui/SideBarLists"] .tc-titlebar {
     display: none;
}

.tc-sidebar-lists p {
     margin-top: 3px;
     margin-bottom: 3px;
}

.tc-btn-mini {
     font-size: 1.2em;
     padding-right: 10px;
}

.tc-topbar svg {
	fill: <<colour background>>;
}

.tc-topbar button:hover svg {
	fill: <<colour foreground>>;
}

tc-sidebarlists .tc-tab-buttons {
     padding-top: 0em;
}

/* Adjust storyriver */
.tc-story-river {
     padding: 50px 21px 21px 21px;
}

.tc-btn-invisible {
     outline:none;
}

/* Remove descriptions of buttons in Tools tab */
.tc-sidebar-lists .tc-muted {
     display:none;
}

hr {
     color: <<colour foreground>>;
}

/* Color active state toggle button */
body.tc-body .tgc-active-indicator {
     color: <<colour alert-highlight>>;
}
body.tc-body .tgc-active-indicator svg {
     fill: <<colour alert-highlight>>;
}
body.tc-body .tgc-active-indicator:hover svg {
     fill: <<colour "foreground">>;
}

/* Recent tab date bold */
.tc-sidebar-lists .tc-timeline {
   font-weight: bold;
}

/* Top bar */
.tc-topbar button {
     padding: 8px;
}
.tgc-topbar {
     position: fixed;
     top:  0px;
     width: 100%;
     left: 0px;
     padding-top: 2px;
     background-color: <<colour primary>>;
}

/* Top bar button size */
html .tgc-topbar svg {
     width: 1.7em;
     height: 1.7em;
}

/* General styles */
/* body prefix required! */
body.tc-body {
     color: <<colour "foreground">>;
     background-color: <<colour "page-background">>; 
}

/* Text styles */
.monospace {
     font-family:  monospace, serif;
}

/* Edit mode styles */
textarea.tc-edit-texteditor, code, kbd, pre, samp {
     font-size: 1em;
}

textarea, input [type="text"] {
     color: <<colour "foreground">>;
}

input.tc-titlebar.tc-edit-texteditor {
     color: <<colour "foreground">>;
}

/* font-size title in edit mode */
input.tc-edit-texteditor {
     font-size: 1.3em;
}

/* font-size 'add tag' */
.tc-edit-add-tag .tc-add-tag-name input {
     font-size: 1.0em;
     width: 50%;
}

/* font-size 'add field' & 'value' */
.tc-edit-field-add-name input.tc-edit-texteditor{
     font-size: 1.0em;
}
.tc-edit-field-add-value input.tc-edit-texteditor{
     font-size: 1.0em;
}

/* Tiddler frame style */
.tc-tiddler-frame {
     background-color: <<colour "page-background">>;
     padding: 0px;
     <<box-shadow none>>
}

/* Title style */
.tc-site-title, .tc-titlebar {
     font-size: 2em;
     font-weight: bold;
     color: <<colour "foreground">>;
}

/* Site title (tagged with $:/tags/AboveStory) */
.story-backdrop {
     margin-top: -10px;
}

.tc-titlebar {
     font-size: 1.9em;
     padding-top: 2px;
}

.tc-tiddler-title {
     font-size: 0.8em;
}

/* Heading styles */
h1, h2, h3, h4, h5, h6 {
     line-height: 1em;
}

h1 {
     font-size: 1.3em;
     font-weight: 700;
}

h2 {
     font-size: 1.2em;
     font-weight: 700;
     margin-bottom:-.2em;
     margin-top:1.2em;
}

h3 {
     font-size: 1.1em;
     font-weight: 700;
     margin-bottom:-.4em;
     margin-top:1.4em;
}

h4 {
     font-size: 1.0em;
     font-variant:small-caps;
     font-weight: 700;
     margin-bottom:-.6em;
     margin-top:1.6em;
}

.tc-sidebar-header {
     text-shadow: none;
}

/* Link styles */
button.tc-tiddlylink, a.tc-tiddlylink {
     color: <<colour "tiddler-link-foreground">>;
     font-weight: normal;
}

a.tc-tiddlylink:hover {
     color: <<colour foreground>>;
     background-color: <<colour muted-foreground>>;
     text-decoration: underline;
}

/* Missing tiddler link */
a.tc-tiddlylink-missing {
     color: #FF0000;
}

/* Shadow tiddler link */
a.tc-tiddlylink-shadow {
     font-weight: bold;
}

/* Overridden shadow tiddler link */
a.tc-tiddlylink-shadow.tc-tiddlylink-resolves {
     font-weight: bold;
     color: #884411;
}

/* Tabs*/

.tc-tab-buttons {
     margin-bottom: -1px;
}

.tc-tab-buttons button {
     font-size: 1.2em;
     color: <<colour "tab-foreground">>;
     margin: 0px;
     background-color: <<colour "tab-background">>;
}

.tc-tab-buttons button.tc-tab-selected {
     font-weight: bold;
     color: <<colour "tab-foreground">>;
     margin: 0px;
     background-color: <<colour "tab-background-selected">>;
}

/* Sidebar tabs */
.tc-sidebar-lists .tc-tab-buttons button {
     font-size: 1.4em;
     color: <<colour "tab-foreground">>;
     padding: 6px 15px;
     margin: 0px;
     background-color: <<colour "tab-background">>;
}

.tc-sidebar-lists .tc-tab-buttons button.tc-tab-selected {
     font-weight: bold;
     color: <<colour "tab-foreground">>;
     padding: 6px 15px;
     margin: 0px;
     background-color: <<colour "tab-background-selected">>;
}

/* Sidebar 'More' tabs */
.tc-more-sidebar > .tc-tab-set > .tc-tab-buttons > button {
     font-size: 1.2em;
     background-image: none;
     background-color: <<colour "tab-background">>;
     padding: 4px 5px;
}
.tc-more-sidebar > .tc-tab-set > .tc-tab-buttons > button.tc-tab-selected {
     background-image: none;
     background-color: <<colour "tab-background-selected">>;
     padding: 4px 5px;
}

/* Tab divider */
.tc-sidebar-lists .tc-tab-divider {
     border-top: 1px solid <<colour "tab-background-selected">>;
}

/* Tiddler Info tabs */
.tc-tiddler-info .tc-tab-buttons button.tc-tab-selected {
     font-weight: bold;
     background-color: <<colour "tab-background-selected">>;
}

/* Sidebar styles */
/* subtitle, date recent tiddlers, search heading + count, tag count, TW version */
.tc-sidebar-header {
     color: <<colour "sidebar-foreground">>;
}

/* entries (links) in Sidebar tabs + x to close search */
.tc-sidebar-lists a.tc-tiddlylink {
     color: <<colour "sidebar-tiddler-link-foreground">>;
     fill: <<colour "sidebar-tiddler-link-foreground">>;
}

.tc-sidebar-lists a.tc-tiddlylink:hover {
     color: <<colour "sidebar-tiddler-link-foreground-hover">>;
     fill: <<colour "sidebar-tiddler-link-foreground-hover">>;
}

/* Sidebar: missing tiddlers */
.tc-sidebar-header .tc-missing-tiddler-label {
     color: #FF0000;
}

/* Brown overridden shadow tiddlers */
.tc-sidebar-lists a.tc-tiddlylink-shadow.tc-tiddlylink-resolves {
     font-weight: bold;
     color: #884411;
}

/* Tools tab, button hint (+ Control panel) */
.tc-muted {
     color: <<colour "muted-foreground">>;
}

/* search entries */
.tc-sidebar-lists input {
     color: <<colour "foreground">>;
}

/* ====== General HTML button style ====== */
button, optgroup, select {
     <<background-linear-gradient "#FFFFFF,#E8EEF9">>
padding: 2px 4px;
     border-radius: 0.3em;
     border: 1px solid <<colour muted-foreground>>
}

/* ====== Sidebar ====== */
/* Page controls button styles */
.tc-page-controls svg {
     fill: <<colour "sidebar-controls-foreground">>;
}

.tc-page-controls button:hover svg,
.tc-page-controls a:hover svg {
     fill: <<colour "sidebar-controls-foreground-hover">>;
}

/* Sidebar Open tab: 'x' & 'close all' style */
.tc-sidebar-lists button.tc-btn-mini {
     color: <<colour "sidebar-muted-foreground">>;
     font-weight: bold;
}

.tc-sidebar-lists button.tc-btn-mini:hover {
     fill: <<colour "sidebar-muted-foreground-hover">>;
     font-weight: bold;
}

/* ====== Tiddler ====== */
/* Tiddler controls button style */
/* html prefix required!! */
html .tc-tiddler-controls svg {
     fill: <<colour "tiddler-controls-foreground">>;
}

.tc-tiddler-controls button.tc-btn-invisible:hover svg {
     fill: <<colour "tiddler-controls-foreground-hover">>;
}

/* More dropdown style */
.tc-tiddler-controls .tc-drop-down a,
.tc-tiddler-controls .tc-drop-down button {
     font-size: 0.9em;
     font-weight: normal;
}


/* ====== Miscellaneous ====== */

/* Tag styles */
/* Default color tag pills */
button.tc-tag-label, span.tc-tag-label {
     background-color: <<colour "tag-background">>;
}

/* Tags dropdown lists */
.tc-tag-list-item .tc-drop-down a,
.tc-drop-down button,
.tc-sidebar-lists .tc-drop-down a,
.tc-sidebar-lists .tc-drop-down button {
     color: <<colour "foreground">>;
     font-size: 1.0em;
}

.tc-tag-list-item .tc-drop-down a:hover,
.tc-sidebar-lists .tc-drop-down a:hover {
     color: <<colour "background">>;
}

/* Blockquote */
blockquote {
     border-left: 5px solid <<colour "muted-foreground">>;
}

/* Slider */
html .tgc-slider {
     color: <<colour "primary">>;
     font-weight: bold;
}

.tgc-slider:hover {
     color: <<colour "foreground">>;
     text-decoration: underline;
}

