{"tiddlers":{"$:/plugins/wikilabs/click-effect/clickEffectStyles":{"title":"$:/plugins/wikilabs/click-effect/clickEffectStyles","tags":"$:/tags/Stylesheet","type":"text/css","text":".pulse {
	background: lightgreen;
	animation: pulse 0.8s ease-out;
	z-index: 99999; 
}

@keyframes pulse {
	0% { opacity: .3; }
	100% { opacity: 1; }
}

.clickEffect {
	position: fixed;
	box-sizing: border-box;
	border-style: solid;
	border-color: #aaa;
	border-radius: 50%;
	animation: clickEffect 0.4s ease-out;
	z-index: 99999;
}

@keyframes clickEffect {
	0% {
		opacity: 1;
		width: 0.5em;
		height: 0.5em;
		margin: -0.25em;
		border-width: 0.5em;
	}
	100% {
		opacity: 0.2;
		width: 5em;
		height: 5em;
		margin: -2.5em;
		border-width: 0.1em;
	}
}"},"$:/config/clickEffect":{"title":"$:/config/clickEffect","text":"clickEffect"},"$:/core/modules/utils/dom-tweaks.js":{"title":"$:/core/modules/utils/dom-tweaks.js","text":"/*\
title: $:/core/modules/utils/dom-tweaks.js
type: application/javascript
module-type: utils

Fix .pulseElement()
Add .clickAnimation()

Various static DOM-related utility functions.
\*/

(function(){
/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
TODO - FIX - should be fixed upstream
Pulse an element for debugging purposes
*/
exports.pulseElementFixed = function(element) {
	var eventName = $tw.utils.convertEventName("animationEnd");
	// Event handler to remove the class at the end
	element.addEventListener(eventName,function handler(event) {
		element.removeEventListener(eventName,handler,false);
		$tw.utils.removeClass(element,"pulse");
	},false);
	// Apply the pulse class
	$tw.utils.removeClass(element,"pulse");
	$tw.utils.forceLayout(element);
	$tw.utils.addClass(element,"pulse");
};

/*
Cursor Click animation. Is useful to create videos
This animation creates its own div element
Options:
clickEffect: CSS class name as string with no special formatting eg: clickEffect
domNode: DOM node, that should contain the animated element. eg: rootElement
event: event Object
*/
exports.clickAnimation = function(options) {
	var options = options || {},
		eventName = $tw.utils.convertEventName("animationEnd"),
		element = document.createElement("div");

	element.className = options.clickEffect || "";
	element.style.top = options.event.clientY+"px";
	element.style.left = options.event.clientX+"px";
	options.domNode.appendChild(element);
		// Event handler to remove the element if animation is finished
	element.addEventListener(eventName,function() {
		element.parentElement.removeChild(element);
		}.bind(this),false);
}

})();","type":"application/javascript","module-type":"utils"},"$:/core/modules/utils/dom/popup-tweaks.js":{"title":"$:/core/modules/utils/dom/popup-tweaks.js","text":"/*\
title: $:/core/modules/utils/dom/popup-tweaks.js
type: application/javascript
module-type: utils

Module that creates a $tw.utils.Popup object prototype that manages popups in the browser

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

// Configuration tiddler, if click animation shuld be shown!
var CONFIG_CLICK_ANIMATION = "$:/config/clickEffect";
var Popup = require("$:/core/modules/utils/dom/popup.js").Popup;

// CHANGE -  CHANGE -  CHANGE -  CHANGE -  CHANGE
Popup.prototype.handleEvent = function(event) {
	if(event.type === "click") {
		// Find out what was clicked on
		var info = this.popupInfo(event.target),
			cancelLevel = info.popupLevel - 1;
		// Don't remove the level that was clicked on if we clicked on a handle
		if(info.isHandle) {
			cancelLevel++;
		}
		// Cancel
		this.cancel(cancelLevel);
		// Show Click Animation
		this.clickAnimation(event);
	}
};

Popup.prototype.pulseElement = function(domNode) {
	$tw.utils.pulseElementFixed(domNode)
};

/*
Animation Options: ... if configured
clickEffect: CSS class name as string with no special formatting eg: clickEffect
domNode: DOM node, that should contain the animated element. eg: rootElement
event: event Object
*/
Popup.prototype.clickAnimation = function(event) {
	var options = {};
	options.event = event;
	var clickEffect = $tw.wiki.getTiddlerText(CONFIG_CLICK_ANIMATION,"no").trim();
	if (clickEffect === "clickEffect") {
		options = {
			clickEffect: "clickEffect",
			domNode: this.rootElement,
			event: event
		}
		$tw.utils.clickAnimation(options);
	} else if (clickEffect === "pulse") {
		this.pulseElement(event.target);
	}
};

})();
