{"tiddlers":{"$:/wikilabs/ui/TiddlerInfo/TagMap":{"title":"$:/wikilabs/ui/TiddlerInfo/TagMap","tags":"$:/tags/TiddlerInfo","caption":"Tag Map","text":"\whitespace trim
<div class="flexbox">
<$list filter="[all[current]tags[]sort[title]]" emptyMessage="This tiddler has no tags">
<div class="col">
<span class="tc-tag-label">
<$transclude tiddler="$:/core/ui/ListItemTemplate"/>
</span>
<hr>
<$list filter="[all[current]tagging[]sort[title]]" template="$:/core/ui/ListItemTemplate" />
</div>
</$list>
</div>"},"$:/wikilabs/ui/TiddlerInfo/TagMap/Styles":{"title":"$:/wikilabs/ui/TiddlerInfo/TagMap/Styles","tags":"$:/tags/Stylesheet","type":"text/plain","text":".flexbox {
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  overflow: hidden;
  flex-direction: row;
  flex-flow: row wrap;
}

.flexbox .col {
  flex: 1 1 33%;
  margin-bottom: 1em;
}"},"$:/plugins/wikilabs/info-tagmap/history":{"title":"$:/plugins/wikilabs/info-tagmap/history","text":"V 0.2.2 - 2024-05-23

